DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `spConsultarPlanillaBuscar`(
	_id_tipo_proceso_planilla BIGINT(20),
        _id_trabajador BIGINT(20)
    )
BEGIN
	DECLARE _id_tipo_planilla BIGINT(20);
	DECLARE _fecha_tipo_proceso_planilla DATE;
	
	SELECT id_tipo_planilla,fecha_tipo_proceso_planilla INTO _id_tipo_planilla,_fecha_tipo_proceso_planilla
		FROM m_tipo_proceso_planilla WHERE id_tipo_proceso_planilla = _id_tipo_proceso_planilla;
	
	SELECT p.id_trabajador, pe.nombrecompleto,tr.fecha_alta,t.tipo_proceso_planilla,p.fecha_proceso_planilla,p.c_sueldo_bruto,
		p.sub_total_ingresos,p.c_tot_ingresos,p.c_tot_descuentos,p.c_sueldo_neto,P.c_tot_contribuciones,p.ci_remun_basica,
		p.ci_asig_familiar,p.ci_sueldo_basico,p.c_porcentage_essalud,p.cd_essalud,p.c_reunif,p.c_transit_x_homolog,p.c_b_pers,
		p.c_bonif_famil,p.c_res_nro_001_2012,p.c_res_nro_122_2012,p.c_bonif_x_movilidad,p.c_ley_29714,
		p.c_dns_276_91,p.c_monto_aporte_sist_pension,p.c_porcentage_sist_pension,p.c_vacaciones,
		p.c_porcent_prima_sist_pension,p.c_monto_prima_seguro_afp,p.c_comision_var_sist_pension,
		p.c_monto_comision_var_afp,p.c_agu_fiestas_patrias,p.c_agu_navidad,p.c_prestamos_total,
		p.c_prestamo_cmac_sull,p.c_prestamo_cmac_paita,p.c_prestamo_cofap,p.c_aporte_cofap,
		p.c_descto_sindical,p.c_nro_inasistencia,p.c_descto_inasistencia,p.c_nro_tardanzas,p.c_descto_tardanzas,
		p.c_descto_judicial,p.c_descto_apoyo,p.c_quinta_categoria,p.c_seguro_rimac,p.c_essalud_vida,p.c_cafae,p.c_adelanto,
		p.c_sistema_pension,sp.sistemapension,p.usuario_creacion
	FROM p_planilla_trabajador p, m_tipo_proceso_planilla t, p_trabajador tr, mae_persona pe, m_sistemapension sp
	WHERE p.id_trabajador = tr.id_trabajador AND tr.id_persona = pe.id_persona AND 
		p.id_tipo_planilla = t.id_tipo_planilla AND p.fecha_tipo_planilla = t.fecha_tipo_proceso_planilla AND 
		p.c_sistema_pension = sp.id_sistemapension AND
		p.id_trabajador = _id_trabajador AND p.id_tipo_planilla = _id_tipo_planilla AND 
		p.fecha_tipo_planilla = _fecha_tipo_proceso_planilla;
		
    END */$$
