/* Procedure structure for procedure `spConsultarPlanillaGrid` */

/*!50003 DROP PROCEDURE IF EXISTS  `spConsultarPlanillaGrid` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `spConsultarPlanillaGrid`(
	_iDisplayStart INT,
        _iDisplayLength INT, 
        _iOrderCol VARCHAR(200),
        _sSearch VARCHAR(200),
        _sFilterCols VARCHAR(300)
    )
BEGIN
	DECLARE orderby VARCHAR(300) DEFAULT '';
	DECLARE isearch VARCHAR(300) DEFAULT '';
	DECLARE filterCols VARCHAR(300) DEFAULT '';
	
	-- IF _sSearch <> '' THEN
		-- SET isearch = CONCAT(' AND m.modulo LIKE "%',_sSearch,'%" ');
	-- END IF;
	IF _sFilterCols <> '' THEN
		SET filterCols = REPLACE(_sFilterCols,'&quot;','"');
		SET filterCols = REPLACE(filterCols,'*','%');
	END IF;
	IF _iOrderCol <> '' THEN
		SET orderby = CONCAT(' ORDER BY ',_iOrderCol);
	END IF;
	
	SET @sentencia = CONCAT('
	SELECT 
		COUNT(*) INTO @countx
	FROM mae_persona
	WHERE estado != "E"
	',isearch,' ',filterCols,' ;');
	
	PREPARE consulta FROM @sentencia;
	EXECUTE consulta;
	DEALLOCATE PREPARE consulta;
	SET @sentencia = NULL;
	
	SET @sentencia = CONCAT('
	SELECT 
		@countx as total,
		p.id_persona,
		p.apellidopaterno,
		p.apellidomaterno,
		p.primernombre,
		p.segundonombre,
		p.estado,
		t.id_trabajador
	FROM p_trabajador t, mae_persona p
	WHERE t.id_persona = p.id_persona and p.estado != "E"
	',isearch,' ',filterCols,' ',orderby,' LIMIT ',_iDisplayStart,',',_iDisplayLength,'; ');
		
	PREPARE consulta FROM @sentencia;
	EXECUTE consulta;
	DEALLOCATE PREPARE consulta;
	SET @sentencia = NULL;
    END */$$
DELIMITER ;

