	DECLARE sql_feriado CHAR(1) DEFAULT '';
	DECLARE sql_diaactual TIME;
	
	SET _horaActual = NOW();
	SET sql_tolerancia = '00:05:00';
	SET sql_fintolerancia = '00:11:00';
	SET sql_cierreTarde ='00:01:00';
	SET sql_diaactual = DAYOFWEEK(CURDATE());
	
	IF _flag = 1 THEN
		#verificamos si el trabajador existe
		SELECT COUNT(*) INTO _existe FROM p_trabajador WHERE huella_digital=_huellaDigital; 		
		
		IF _existe>0 THEN
			#obtenemos los id de las tablas involucradas
			SELECT id_asistenciames,feriado INTO sql_idAsistenciames,sql_feriado FROM m_asisenciames WHERE fecha=CURDATE();
			SELECT id_trabajador INTO sql_idtrabajador FROM p_trabajador t WHERE t.huella_digital=_huellaDigital;
						
			#verificamos si la asistencia ya fue registrada
			SELECT COUNT(*) INTO sql_total FROM p_asistenciatrabajador WHERE id_asistenciames=sql_idAsistenciames AND id_asistenciatrabajador=sql_idtrabajador AND estado<>'E';				
			
			#verificamos si el trabajador tiene configurado hora de ingreso
			SELECT COUNT(*) INTO _tienehoras FROM p_trabajador t
			INNER JOIN p_horario h ON t.id_horario=h.id_horario
			WHERE t.huella_digital=_huellaDigital AND 
				h.hora_ingreso_m='00:00:00' AND
				h.hora_ingreso_t='00:00:00';
			
			IF _tienehoras > 0 THEN  #casos sin horario
									
				IF(_horaActual<'14:00:00') THEN  #hora de maniana
					IF sql_total = 0 THEN
						INSERT INTO p_asistenciatrabajador(id_asistenciames,id_trabajador,hora_ingreso_m,origen,estado)
						VALUES(sql_idAsistenciames,sql_idtrabajador,_horaActual,'H','A');
					ELSE
						UPDATE p_asistenciatrabajador SET
							hora_salida_m=_horaActual
						WHERE id_asistenciames=sql_idAsistenciames AND 
							id_trabajador=sql_idtrabajador AND
							estado<>'E'; 
					END IF;
				ELSE #hora de la tarde
					IF sql_total = 0 THEN
						INSERT INTO p_asistenciatrabajador(id_asistenciames,id_trabajador,hora_ingreso_t,origen,estado)
						VALUES(sql_idAsistenciames,sql_idtrabajador,_horaActual,'H','A');
					ELSE
						SELECT COUNT(*) INTO sql_verificaTarde FROM p_asistenciatrabajador 
						WHERE id_asistenciames=sql_idAsistenciames AND 
							id_trabajador=sql_idtrabajador AND
							estado<>'E' AND 
							hora_ingreso_t='00:00:00';
							
						IF sql_verificaTarde = 0 THEN
							UPDATE p_asistenciatrabajador SET
								hora_ingreso_t=_horaActual
							WHERE id_asistenciames=sql_idAsistenciames AND 
								id_trabajador=sql_idtrabajador AND
								estado<>'E';
							
						ELSE
							UPDATE p_asistenciatrabajador SET
								hora_salida_t=_horaActual
							WHERE id_asistenciames=sql_idAsistenciames AND 
								id_trabajador=sql_idtrabajador AND
								estado<>'E';
									
						END IF;
					
					END IF;
				END IF;
				
				
			ELSE
				#verificamos si el dia es laborable
				IF sql_feriado = '0' THEN
				
					#verificamos si es sabado o domingo
					IF sql_diaactual !=1 AND sql_diaactual!=7 THEN
					
						#obtenemos el horario de ingreso y salida
						SELECT h.hora_ingreso_m,h.hora_salida_m,h.hora_ingreso_t,h.hora_salida_t INTO sql_manianaI,sql_manianaS,sql_tardeI,slq_tardeS
						FROM p_trabajador t
						INNER JOIN p_horario h ON h.id_horario=t.id_horario
						WHERE huella_digital=_huellaDigital;
						
						#verificamos si el registro pertenece a la maniana o la tarde
						IF sql_manianaI <= _horaActual AND _horaActual<=sql_manianaS THEN
						
							#si no existe lo registramos
							IF sql_total = 0 THEN 
								#obtenemos el inicio de la tardanza
								SET sql_iniciotolerancia = ADDTIME(sql_manianaI, sql_tolerancia);
								SET sql_limitetolerancia = ADDTIME(sql_manianaI, sql_fintolerancia);
								
								IF _horaActual <=sql_iniciotolerancia THEN							
									INSERT INTO p_asistenciatrabajador(id_asistenciames,id_trabajador,hora_ingreso_m,origen,estado)
									VALUES(sql_idAsistenciames,sql_idtrabajador,_horaActual,'H','A');							
									
								ELSEIF(_horaActual>sql_iniciotolerancia AND _horaActual<sql_limitetolerancia) THEN
									INSERT INTO p_asistenciatrabajador(id_asistenciames,id_trabajador,hora_ingreso_m,origen,tardanza,tardanza_m,estado)
									VALUES(sql_idAsistenciames,sql_idtrabajador,_horaActual,'H','1',TIMEDIFF(_horaActual, sql_iniciotolerancia),'A');
									
								ELSE
									SET _result  = 4; -- EL ingreso a sido Cerrado
								END IF;
								
							ELSE
								IF(_horaActual<=sql_limitetolerancia) THEN
									SET _result = 2; -- El registro ya existe
									
								ELSE
									UPDATE p_asistenciatrabajador SET
										hora_salida_m=_horaActual
									WHERE id_asistenciames=sql_idAsistenciames AND 
										id_trabajador=sql_idtrabajador AND
										estado<>'E';
									
								END IF;
								
							END IF;
							
							
						ELSEIF sql_tardeI <= _horaActual AND _horaActual<=slq_tardeS THEN  #registro horario tarde
							#verificamos si la asistencia ya fue registrada
							IF sql_total = 0 THEN
								SET sql_bloqueoTarde = ADDTIME(sql_tardeI,sql_cierreTarde);
								IF(_horaActual<sql_bloqueoTarde) THEN 
									INSERT INTO p_asistenciatrabajador(id_asistenciames,id_trabajador,hora_ingreso_t,origen,estado)
									VALUES(sql_idAsistenciames,sql_idtrabajador,_horaActual,'H','A');	
								
								ELSE
									SET _result = 4; -- Horario de ingreso Cerrado	
									
								END IF;
							ELSE
								IF(_horaActual<=sql_bloqueoTarde) THEN
								       #verificamos si el horario de la tarde ya fue registrado
									SELECT COUNT(*) INTO sql_verificaTarde FROM p_asistenciatrabajador 
									WHERE id_asistenciames=sql_idAsistenciames AND 
										id_trabajador=sql_idtrabajador AND
										estado<>'E' AND 
										hora_ingreso_t='00:00:00';
										
									IF sql_verificaTarde = 0 THEN
										UPDATE p_asistenciatrabajador SET
											hora_ingreso_t=_horaActual
										WHERE id_asistenciames=sql_idAsistenciames AND 
											id_trabajador=sql_idtrabajador AND
											estado<>'E';
									ELSE
										SET _result = 2; -- El registro ya existe
									END IF;	
									
								ELSE
									UPDATE p_asistenciatrabajador SET
										hora_salida_t=_horaActual
									WHERE id_asistenciames=sql_idAsistenciames AND 
										id_trabajador=sql_idtrabajador AND
										estado<>'E';
									
								END IF;		
							END IF;
							
						ELSE
							SET _result = 5; --  Fuera de horario
						END IF;
					ELSE 
						SET _result = 5;  -- Dia no laborable por se sabado y domingo
					END IF;
				ELSE
					SET _result = 6;  -- Dia no laborable
				END IF;	
			END IF;
		ELSE
			SET _result = 3; # Trabajador no identificado
		END IF;
		
		SELECT _result AS result,_duplicado AS duplicado;
	END IF;
END */$$
DELIMITER ;

