		FROM m_asisenciames e 
		WHERE e.estado="A" and
	left(e.fecha,7)="',_mesAnio,'"
	',isearch,' ',filterCols,' ',orderby,'
	) k LIMIT ',_iDisplayStart,',',_iDisplayLength,'; ');
	-- SELECT @sentencia;	
	PREPARE consulta FROM @sentencia;
	EXECUTE consulta;
	DEALLOCATE PREPARE consulta;
	SET @sentencia = NULL;
	
END */$$
DELIMITER ;

/* Procedure structure for procedure `sp_conasGenerarMesGrid` */

/*!50003 DROP PROCEDURE IF EXISTS  `sp_conasGenerarMesGrid` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_conasGenerarMesGrid`(
	_iDisplayStart INT,
        _iDisplayLength INT, 
        _iOrderCol VARCHAR(200),
        _sSearch VARCHAR(200),
        _sFilterCols VARCHAR(300)
    )
BEGIN
	DECLARE orderby VARCHAR(300) DEFAULT '';
	DECLARE isearch VARCHAR(300) DEFAULT '';
	DECLARE filterCols VARCHAR(300) DEFAULT '';
		
	IF _sFilterCols <> '' THEN
		SET filterCols = REPLACE(_sFilterCols,'&quot;','"');
		SET filterCols = REPLACE(filterCols,'*','%');
	END IF;
		
	SET @sentencia = CONCAT('
	select count(*) into @countx From (
		SELECT 
			LEFT(fecha,4)AS anio,
			fnNombreMes(fecha)AS mes,
			LEFT(fecha,7)AS orden,
			estado,
			id_asistenciames,
			left(fecha,7) as mesanio
		FROM m_asisenciames
		WHERE estado != "E"
		',filterCols,'
		GROUP BY 1,2
	)k;
	');
	
	PREPARE consulta FROM @sentencia;
	EXECUTE consulta;
	DEALLOCATE PREPARE consulta;
	SET @sentencia = NULL;
	
	
	DROP TABLE IF EXISTS tmp_asistencias_;
	CREATE TEMPORARY TABLE tmp_asistencias_(INDEX(mesanio),feriados INT DEFAULT 0)
	SELECT 
		LEFT(fecha,4)AS anio,
		fnNombreMes(fecha)AS mes,
		LEFT(fecha,7)AS orden,
		estado,
		id_asistenciames,
		LEFT(fecha,7) AS mesanio,
		@countx AS total
	FROM m_asisenciames
	WHERE estado != "E"
	GROUP BY 1,2
	ORDER BY 1 DESC,3;
	DROP TABLE IF EXISTS tmp_misFeriados_;
	CREATE TEMPORARY TABLE tmp_misFeriados_(INDEX(mesanio))
	SELECT  COUNT(*) AS feriados,LEFT(fecha,7) AS mesanio
	FROM m_asisenciames 
	WHERE feriado='1'
	GROUP BY 2;
	UPDATE tmp_asistencias_ a
	INNER JOIN tmp_misFeriados_ b ON a.mesanio=b.mesanio
	SET a.feriados=b.feriados;
	SET @sentencia = CONCAT('
	SELECT * from tmp_asistencias_ 
	where 1=1
	',filterCols,'
	LIMIT ',_iDisplayStart,',',_iDisplayLength,'; ');
	
	
	/*
	SET @sentencia = CONCAT('
	SELECT 
		LEFT(fecha,4)AS anio,
		fnNombreMes(fecha)AS mes,
		LEFT(fecha,7)AS orden,
		estado,
		id_asistenciames,
		left(fecha,7) as mesanio,
		@countx as total
	FROM m_asisenciames
	WHERE estado != "E"
	',filterCols,'
	GROUP BY 1,2
	ORDER BY 1 DESC,3
	LIMIT ',_iDisplayStart,',',_iDisplayLength,'; ');
	*/
		
	PREPARE consulta FROM @sentencia;
	EXECUTE consulta;
	DEALLOCATE PREPARE consulta;
	SET @sentencia = NULL;
    END */$$
DELIMITER ;

/* Procedure structure for procedure `sp_conasGenerarMesMantenimiento` */

/*!50003 DROP PROCEDURE IF EXISTS  `sp_conasGenerarMesMantenimiento` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_conasGenerarMesMantenimiento`(
	_flag int,
	_key int,
	_anio varchar(4),
	_usuario char(8)
    )
BEGIN
	declare _result int default 1;
	declare _duplica int default 0;
	declare _c int;
	declare _mes char(2) default '';
	declare _fechaIni date;
	declare _fechaFin date;
	declare _idAsistenciaMesLast int;
	
	-- INSERT
	if _flag = 1 then
		SET _fechaIni = CONCAT(_anio,'-01-01');
		SET _fechaFin = CONCAT(_anio,'-12-31');
		
		-- duplicidad
		SELECT COUNT(*) INTO _duplica FROM m_asisenciames WHERE left(fecha,4)=_anio;
		IF _duplica = 0 THEN
			WHILE _fechaIni <= _fechaFin DO	
				INSERT INTO m_asisenciames(
					fecha,
					usuario_creacion
				)VALUES(
					_fechaIni,
					_usuario
				);		
				SELECT ADDDATE(_fechaIni,INTERVAL 1 DAY) INTO _fechaIni;
			END WHILE;
		ELSE 
			SET _result = 2; -- Ya Existe 
		END IF;	
		
		select _result as result, _duplica as duplicado;
	end if;
    END */$$
DELIMITER ;

/* Procedure structure for procedure `sp_confUsuarioConsultas` */

/*!50003 DROP PROCEDURE IF EXISTS  `sp_confUsuarioConsultas` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_confUsuarioConsultas`(
	_flag CHAR(1),
	_criterio1 VARCHAR(100),
	_criterio2 VARCHAR(100)
    )
BEGIN
	IF _flag= '1' THEN -- login de usuario
		SELECT 
			u.id_usuario,
			u.usuario,
			p.nombrecompleto,
			p.primernombre,
			p.`id_persona`,
			p.apellidopaterno
		FROM mae_usuario u 
		INNER JOIN mae_persona p ON p.persona=u.persona
		WHERE u.usuario=_criterio1 AND u.clave=_criterio2 AND u.activo='1';
	END IF;
	IF _flag= '2' THEN -- roles de un usuario
		SELECT 
			ur.`id_rol`,
			r.`rol`
		FROM `men_usuariorol` ur
		INNER JOIN men_rol r ON r.id_rol=ur.`id_rol`
		WHERE id_usuario = _criterio1 AND r.`activo`='1';
	END IF;
	IF _flag= '3' THEN -- opciones de menu de un rol
		SELECT
			ro.id_rolopciones,
			d.id_dominio,
			d.dominio,
			m.id_modulo,
			m.modulo,m.orden,
			mp.id_menuprincipal,
			mp.`menuprincipal`,
			mp.url,
			mp.alias AS aliasopcion,
			d.icon
		FROM `men_rolopciones` ro
		INNER JOIN `men_menuprincipal` mp ON mp.`id_menuprincipal`=ro.`id_menuprincipal`
		INNER JOIN `men_modulo` m ON m.id_modulo=mp.id_modulo
		INNER JOIN men_dominio d ON d.id_dominio=m.id_dominio
		WHERE ro.id_rol = _criterio1 AND d.activo='1' AND m.activo='1' AND mp.activo='1'
		ORDER BY d.orden,m.orden,mp.orden;
	END IF;
	IF _flag= '4' THEN -- acciones de una opcion de menu de un rol
		SELECT 
			ao.id_acciones,
			a.accion,
			a.alias AS aliasaccion,
			ao.activo,
			a.icono,
			a.theme
		FROM `men_accionopciones` ao
		INNER JOIN `men_acciones` a ON a.id_acciones=ao.id_acciones
		WHERE ao.id_rolopciones=_criterio1 AND a.activo='1';
	END IF;
	IF _flag= '5' THEN -- tablas de una db
		SET @sentencia = CONCAT('
		SHOW TABLE STATUS FROM ',_criterio1,'; ');
			
		PREPARE consulta FROM @sentencia;
		EXECUTE consulta;
		DEALLOCATE PREPARE consulta;
		SET @sentencia = NULL;		
	END IF;
	IF _flag= '6' THEN -- columnas de tabla de una db
		SET @sentencia = CONCAT('
		SELECT COLUMN_NAME
		FROM information_schema.COLUMNS
		WHERE TABLE_SCHEMA LIKE "',_criterio1,'" AND TABLE_NAME = "',_criterio2,'"; ');
			
		PREPARE consulta FROM @sentencia;
		EXECUTE consulta;
		DEALLOCATE PREPARE consulta;
		SET @sentencia = NULL;		
	END IF;
    END */$$
DELIMITER ;

/* Procedure structure for procedure `sp_consModuloGrid` */

/*!50003 DROP PROCEDURE IF EXISTS  `sp_consModuloGrid` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_consModuloGrid`(
	_iDisplayStart INT,
        _iDisplayLength INT, 
        _iOrderCol VARCHAR(200),
        _sSearch VARCHAR(200),
        _sFilterCols varchar(300)
    )
BEGIN
	DECLARE orderby VARCHAR(300) DEFAULT '';
	DECLARE isearch VARCHAR(300) DEFAULT '';
	DECLARE filterCols VARCHAR(300) DEFAULT '';
	
	IF _sSearch <> '' THEN
		SET isearch = CONCAT(' AND m.modulo LIKE "%',_sSearch,'%" ');
	END IF;
	IF _sFilterCols <> '' THEN
		SET filterCols = replace(_sFilterCols,'&quot;','"');
		SET filterCols = REPLACE(filterCols,'*','%');
	END IF;
	IF _iOrderCol <> '' THEN
		SET orderby = CONCAT(' ORDER BY ',_iOrderCol);
	END IF;
	
	SET @sentencia = CONCAT('
	SELECT 
		COUNT(*) INTO @countx
	FROM `c_modulo` m
	INNER JOIN `mae_usuario` u ON u.`id_usuario`=m.`usuario_creacion`
	INNER JOIN `c_aplicacion` a ON a.id_aplicacion=m.id_aplicacion
	WHERE m.`estado` = "A" ',isearch,' ',filterCols,' ;');
	
	PREPARE consulta FROM @sentencia;
	EXECUTE consulta;
	DEALLOCATE PREPARE consulta;
	SET @sentencia = NULL;
	
	SET @sentencia = CONCAT('
	SELECT
		@countx as total,
		m.id_modulo,
		a.aplicacion,
		m.modulo,
		m.estado,
		u.`usuario`,
		DATE_FORMAT(m.`fecha_creacion`,"%d-%m-%Y %h:%m:%s")AS fecha_creacion
	FROM `c_modulo` m
	INNER JOIN `mae_usuario` u ON u.`id_usuario`=m.`usuario_creacion`
	INNER JOIN `c_aplicacion` a ON a.id_aplicacion=m.id_aplicacion
	WHERE m.`estado` = "A"
	',isearch,' ',filterCols,' ',orderby,' LIMIT ',_iDisplayStart,',',_iDisplayLength,'; ');
		
	PREPARE consulta FROM @sentencia;
	EXECUTE consulta;
	DEALLOCATE PREPARE consulta;
	SET @sentencia = NULL;
    END */$$
DELIMITER ;

/* Procedure structure for procedure `sp_consModuloMantenimiento` */

/*!50003 DROP PROCEDURE IF EXISTS  `sp_consModuloMantenimiento` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_consModuloMantenimiento`(
	_flag char(1),
	_idModulo int,
	_modulo varchar(100),
	_idAplicacion int,
	_grabarDB char(1),
	_usuario char(8)
    )
BEGIN
	declare _duplicidad varchar(100) default '';
	declare _result int default 1;
	
	if _flag = '1' then
		-- duplicidad
		select count(*) into _duplicidad from `c_modulo` where `modulo`=_modulo;
		if _duplicidad = 0 then
			insert into `c_modulo`(
				`id_aplicacion`,
				`modulo`,
				`creardb`,
				`usuario_creacion`
			)values(
				_idAplicacion,
				_modulo,
				_grabarDB,
				_usuario
			);
		else
			set _result = 2; -- duplicado
		end if;
		
		select 
			`ruta`,			
			_result as result,
			_modulo as modulo,
			_duplicidad as duplicado
		from `c_aplicacion` where `id_aplicacion`=_idAplicacion;
	end if;
    END */$$
DELIMITER ;

/* Procedure structure for procedure `sp_maeActividadGrid` */

/*!50003 DROP PROCEDURE IF EXISTS  `sp_maeActividadGrid` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_maeActividadGrid`(
	_iDisplayStart INT,
        _iDisplayLength INT, 
        _iOrderCol VARCHAR(200),
        _sSearch VARCHAR(200),
        _sFilterCols VARCHAR(300)
    )
BEGIN
	DECLARE orderby VARCHAR(300) DEFAULT '';
	DECLARE isearch VARCHAR(300) DEFAULT '';
	DECLARE filterCols VARCHAR(300) DEFAULT '';
	
	-- IF _sSearch <> '' THEN
		-- SET isearch = CONCAT(' AND m.modulo LIKE "%',_sSearch,'%" ');
	-- END IF;
	IF _sFilterCols <> '' THEN
		SET filterCols = REPLACE(_sFilterCols,'&quot;','"');
		SET filterCols = REPLACE(filterCols,'*','%');
	END IF;
	IF _iOrderCol <> '' THEN
		SET orderby = CONCAT(' ORDER BY ',_iOrderCol);
	END IF;
	
	SET @sentencia = CONCAT('
	SELECT
		COUNT(*) INTO @countx
	FROM
	(
		SELECT 	
			a.id_actividad,
			a.actividad,
			e.codigo,
			e.clasificadorderivado,
			a.estado as estadoact
		FROM m_actividad a
		INNER JOIN m_clasificadorderivado e ON e.id_clasificadorderivado = a.id_clasificadorderivado
	)AS F
	WHERE estadoact != "E"
	',isearch,' ',filterCols,' ;');
	
	PREPARE consulta FROM @sentencia;
	EXECUTE consulta;
	DEALLOCATE PREPARE consulta;
	SET @sentencia = NULL;
	
	SET @sentencia = CONCAT('
	SELECT
		total,
		id_actividad,
		actividad,
		codigo,
		clasificadorderivado,
		estadoact
	FROM
	(
		SELECT 	
			@countx as total,
			a.id_actividad,
			a.actividad,
			e.codigo,
			e.clasificadorderivado,
			a.estado as estadoact
		FROM m_actividad a
		INNER JOIN m_clasificadorderivado e ON e.id_clasificadorderivado = a.id_clasificadorderivado
	)AS F
	WHERE estadoact != "E"
	',isearch,' ',filterCols,' ',orderby,' LIMIT ',_iDisplayStart,',',_iDisplayLength,'; ');
		
	PREPARE consulta FROM @sentencia;
	EXECUTE consulta;
	DEALLOCATE PREPARE consulta;
	SET @sentencia = NULL;
    END */$$
DELIMITER ;

/* Procedure structure for procedure `sp_maeActividadMantenimiento` */

