/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_maeActividadMantenimiento`(
	_flag CHAR(1),
	_key INT,
	_actividad VARCHAR(200),
	_idClasificador bigint(20),
	_estado CHAR(1),
	_usuario CHAR(8)
    )
BEGIN
	DECLARE _result INT DEFAULT 1;
	DECLARE _duplicado INT DEFAULT 0;
	
	-- INSERT --
	IF _flag = '1' THEN -- nuevo
		SELECT COUNT(*) INTO _duplicado FROM m_actividad WHERE REPLACE(actividad,' ','') = REPLACE(_actividad,' ','')
									AND estado != 'E'
									and id_clasificadorderivado = _idClasificador;
								
		IF _duplicado = 0 THEN
			INSERT INTO m_actividad(
				id_clasificadorderivado,
				actividad,
				estado,
				usuario_creacion
			)VALUES(
				_idClasificador,
				_actividad,
				_estado,
				_usuario
			);
		ELSE 
			SET _result = 2; -- duplicado
		END IF;
		SELECT _result AS result,_duplicado AS duplicado;
	END IF;
	-- UPDATE --
	IF _flag = '2' THEN
		SELECT COUNT(*) INTO _duplicado FROM m_actividad WHERE REPLACE(actividad,' ','') = REPLACE(_actividad,' ','')
									AND estado != 'E'
									AND id_clasificadorderivado = _idClasificador
									AND id_actividad != _key;
		
		IF _duplicado = 0 THEN
			UPDATE m_actividad SET
				actividad = _actividad,
				estado = _estado,
				usuario_modifica = _usuario,
				fecha_modifica = NOW()
			WHERE id_actividad = _key;
		ELSE -- duplicado
			SET _result = 2; 
		END IF;
		SELECT _result AS result,_duplicado AS duplicado;
	END IF;
	-- LOGIC DELETE --
	IF _flag = '3' THEN
		UPDATE m_actividad SET estado = 'E' WHERE id_actividad = _key;
		SELECT _result AS result;
	END IF;
    END */$$
DELIMITER ;

