/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_maeCalculaPlanillaTrabajador`(
	_flag CHAR(1),
	_id_tipo_proceso_planilla INT, -- id que contine id_tipo_planilla, fecha_tipo_planilla...
	_usuario CHAR(8)
    )
BEGIN
	-- CALL sp_maeCalculaPlanillaTrabajador(1,1,CURDATE(),'FCL')
	DECLARE _count_concepto_tra INT;
	DECLARE _fecha_inicio_aus DATE; 
	DECLARE _fecha_fin_aus DATE;
	DECLARE _dias_ausencia INT;
	DECLARE _contadorau INT;
	DECLARE _contadorpl INT;
	DECLARE _c INT;
	DECLARE _cpl INT;
	DECLARE _diafal INT;
	DECLARE _fec_fin_faltas DATE;
	DECLARE _fec_ini_faltas DATE;
	DECLARE c_3_dias_1_tar DOUBLE(10,2);
	DECLARE _diatar INT;
	DECLARE _fec_fin_tardanzas DATE;
	DECLARE _fec_ini_tardanzas DATE;
	DECLARE _id_tipo_planilla INT;	-- 1: planilla mensual
	DECLARE _fecha_tipo_planilla DATE;
