	DECLARE _id_persona BIGINT(20);
	DECLARE _id_sistemapension BIGINT(20);
	DECLARE _fecha_alta DATE;
	DECLARE _id_tipocontrato BIGINT(20);
	
	DECLARE _id_conceptoplanilla BIGINT(20);
	DECLARE _descripcion_corta VARCHAR(100);
	DECLARE _monto_asignado DOUBLE(15,3);
	
	DECLARE _conta_proceso INT;
	
	-- CONCEPTOS INGRESOS
	DECLARE _ci_remun_basica DOUBLE(15,3) DEFAULT 0;
	DECLARE _c_reunif DOUBLE(15,3) DEFAULT 0;
	DECLARE _c_transit_x_homolog DOUBLE(15,3) DEFAULT 0;
	DECLARE _c_b_pers DOUBLE(15,3) DEFAULT 0;
	DECLARE _c_bonif_famil DOUBLE(15,3) DEFAULT 0;
