	DECLARE _c_res_nro_122_2012 DOUBLE(15,3) DEFAULT 0;
	DECLARE _c_bonif_x_movilidad DOUBLE(15,3) DEFAULT 0;
	DECLARE _c_indicador_asig_fam INT(1) DEFAULT 0;
