	DECLARE _c_agu_fiestas_patrias DOUBLE(15,3) DEFAULT 0;
	DECLARE _c_agu_navidad DOUBLE(15,3) DEFAULT 0;
	DECLARE _sub_total DOUBLE(15,3) DEFAULT 0;
	DECLARE _c_tot_ingresos DOUBLE(15,3) DEFAULT 0;
	DECLARE _c_sueldo_neto DOUBLE(15,3) DEFAULT 0;
	DECLARE _c_vacaciones DOUBLE(15,3) DEFAULT 0;
	DECLARE _c_ley_29714 DOUBLE(15,3) DEFAULT 0;
	DECLARE _c_dns_276_91 DOUBLE(15,3) DEFAULT 0;
	-- CONCEPTOS INGRESOS
	
	-- EGRESOS
	DECLARE _c_monto_aporte_sist_pension DOUBLE(15,3) DEFAULT 0;
	DECLARE _var_aporte_porcentage VARCHAR(50);
	DECLARE _c_porcentage_afp_snp DOUBLE(15,3) DEFAULT 0;
	DECLARE _c_aporte_afp_onp DOUBLE(15,3) DEFAULT 0;
	
	DECLARE _var_prima_porcentage VARCHAR(50);
	DECLARE _c_prima_afp_snp DOUBLE(15,3) DEFAULT 0;
	DECLARE _c_monto_prima_afp_onp DOUBLE(15,3) DEFAULT 0;
	
	DECLARE _var_comision_variable VARCHAR(50);
	DECLARE _c_comision_variable DOUBLE(15,3) DEFAULT 0;
	DECLARE _c_monto_comision_var_afp DOUBLE(15,3) DEFAULT 0;
	
	DECLARE _c_prestamos_total DOUBLE(15,3) DEFAULT 0;
	DECLARE _c_aporte_cofap DOUBLE(15,3) DEFAULT 0;
	DECLARE _c_descto_sindical DOUBLE(15,3) DEFAULT 0;
	DECLARE _c_nro_inasistencia INT DEFAULT 0;
	DECLARE _c_descto_inasistencia DOUBLE(15,3) DEFAULT 0;
	DECLARE _tipo_aplicacion CHAR(1);
	DECLARE _monto_judicial DOUBLE(15,3) DEFAULT 0;
	DECLARE _contadh INT DEFAULT 0;
	DECLARE _c_descto_judicial DOUBLE(15,3) DEFAULT 0;
	DECLARE _c_descto_apoyo DOUBLE(15,3) DEFAULT 0;
	DECLARE _c_quinta_categoria DOUBLE(15,3) DEFAULT 0;
	DECLARE _c_seguro_rimac DOUBLE(15,3) DEFAULT 0;
	DECLARE _c_essalud_vida DOUBLE(15,3) DEFAULT 0;
	DECLARE _c_sistema_pension INT;
	DECLARE _c_nro_tardanzas INT DEFAULT 0;
	DECLARE _c_nro_tarde INT DEFAULT 0;
	DECLARE _c_descto_tardanzas DOUBLE(15,3) DEFAULT 0;
	DECLARE _c_nro_faltas INT DEFAULT 0;
	DECLARE _c_descto_faltas DOUBLE(15,3) DEFAULT 0;
	DECLARE _c_cafae DOUBLE(15,3) DEFAULT 0;
	DECLARE _c_adelanto DOUBLE(15,3) DEFAULT 0;
	
	DECLARE _c_tot_descuentos DOUBLE(15,3) DEFAULT 0;
	
	DECLARE _cd_essalud DOUBLE(15,3) DEFAULT 0;
	DECLARE _c_porcentage_essalud DOUBLE(15,3) DEFAULT 0;
	
	-- EGRESOS
	
	-- CONTRIBUCIONES
	DECLARE _c_tot_contribuciones DOUBLE(15,3) DEFAULT 0;
	
	-- CONTRIBUCIONES
	DECLARE finp INT DEFAULT 0;
	DECLARE fin INT DEFAULT 0;
	
	-- fecha_baja >= CURDATE() 
	
	
	DROP TEMPORARY TABLE IF EXISTS temp_planilla_trabajador;
	CREATE TEMPORARY TABLE temp_planilla_trabajador(
	contador BIGINT(12) PRIMARY KEY AUTO_INCREMENT,
	tid_trabajador BIGINT(20),
	tid_persona BIGINT(20), 
	tfecha_alta DATE,
	tid_sistemapension BIGINT(20),
	tid_tipocontrato BIGINT(20)
	);
	
	
	DROP TEMPORARY TABLE IF EXISTS temp_ausencias_trabajador;
	CREATE TEMPORARY TABLE temp_ausencias_trabajador(
	contador BIGINT(12) PRIMARY KEY AUTO_INCREMENT,
	fecha_inicio DATE, 
	fecha_fin DATE,
	dias_ausencia INT
	);
	
	
	INSERT INTO temp_planilla_trabajador (tid_trabajador, tid_persona,tfecha_alta,tid_sistemapension,tid_tipocontrato) 
	SELECT id_trabajador,id_persona,fecha_alta,id_sistemapension,id_tipocontrato FROM p_trabajador 
	WHERE estado = 'A' AND fecha_alta <> '0000-00-00' AND 
	(fecha_baja >= CURDATE() OR fecha_baja = '0000-00-00');
	
	SELECT COUNT(*) INTO _contadorpl FROM temp_planilla_trabajador;
	
	SET _cpl = 0;
	WHILE (_cpl < _contadorpl) DO	 
	SET _cpl=_cpl + 1;
	SELECT tid_trabajador, tid_persona,tfecha_alta,tid_sistemapension,tid_tipocontrato INTO _id_trabajador,_id_persona,_fecha_alta,_id_sistemapension,_id_tipocontrato 
	FROM temp_planilla_trabajador WHERE contador = _cpl;
	 
	 
	 -- CONCEPTOS INGRESOS
	SET _count_concepto_tra = 0;	 
	SET _ci_remun_basica = 0;
	SET _c_reunif = 0;
	SET _c_transit_x_homolog = 0;
	SET _c_b_pers = 0;
	SET _c_bonif_famil = 0;
	SET _c_res_nro_001_2012 = 0;
	SET _c_res_nro_122_2012 = 0;
	SET _c_bonif_x_movilidad = 0;
	SET _c_indicador_asig_fam = 0;
	SET _ci_asig_familiar = 0;
	SET _ci_sueldo_basico = 0;
	SET _c_agu_fiestas_patrias = 0;
	SET _c_agu_navidad = 0;
	SET _sub_total = 0;
	SET _c_tot_ingresos = 0;
	SET _c_sueldo_neto = 0;
	SET _c_vacaciones = 0;
	SET _c_ley_29714 = 0;
	SET _c_dns_276_91 = 0;
	-- CONCEPTOS INGRESOS
	
	-- EGRESOS
	SET _c_monto_aporte_sist_pension = 0;
	SET _var_aporte_porcentage = 0;
	SET _c_porcentage_afp_snp = 0;
	SET _c_aporte_afp_onp = 0;
	
	SET _var_prima_porcentage = 0;
	SET _c_prima_afp_snp = 0;
	SET _c_monto_prima_afp_onp = 0;
	
	SET _var_comision_variable = 0;
	SET _c_comision_variable = 0;
	SET _c_monto_comision_var_afp = 0;
	
	SET _c_prestamos_total = 0;
	SET _c_aporte_cofap = 0;
	SET _c_descto_sindical = 0;
	SET _c_nro_inasistencia = 0;
	SET _c_descto_inasistencia = 0;
	SET _tipo_aplicacion = "";
	SET _monto_judicial = 0;
	SET _contadh = 0;
	SET _c_descto_judicial = 0;
	SET _c_descto_apoyo = 0;
	SET _c_quinta_categoria = 0;
	SET _c_seguro_rimac = 0;
	SET _c_essalud_vida = 0;
	SET _c_sistema_pension = 0;
	SET _c_nro_tardanzas = 0;
	SET _c_nro_tarde = 0;
	SET _c_descto_tardanzas = 0;
	SET _c_nro_faltas = 0;
	SET _c_descto_faltas = 0;
	SET _c_cafae = 0;
	SET _c_adelanto = 0;
	
	SET _c_tot_descuentos = 0;
	
	SET _cd_essalud = 0;
	SET _c_porcentage_essalud = 0;
	
	-- EGRESOS
	
	-- CONTRIBUCIONES
	SET _c_tot_contribuciones = 0;
	
	-- DATOS DEL PROCESO PLANILLA
	SELECT id_tipo_planilla,fecha_tipo_proceso_planilla 
	INTO _id_tipo_planilla,_fecha_tipo_planilla
	FROM m_tipo_proceso_planilla WHERE id_tipo_proceso_planilla = _id_tipo_proceso_planilla;
	-- fin DATOS DEL PROCESO PLANILLA
	
	-- VERIFICA EL NUMERO DE TARDANZAS PARA EL MES DE EJECUCIOON DE LA PLANILLA
	SET _diatar = _id_tipo_planilla - 1;
	SET _fec_ini_tardanzas = SUBDATE(_fecha_tipo_planilla, INTERVAL _diatar DAY);
	SET _fec_fin_tardanzas = LAST_DAY(_fecha_tipo_planilla);
	
	SELECT COUNT(*) INTO _c_nro_tarde
	FROM p_asistenciatrabajador a, m_asisenciames m
	WHERE a.id_asistenciames = m.id_asistenciames AND a.id_trabajador = _id_trabajador 
	AND (m.fecha >= _fec_ini_tardanzas AND m.fecha <= _fec_fin_tardanzas)
	AND a.tardanza = 1;
	-- FIN
	
	-- VERIFICA EL NUMERO DE FALTAS PARA EL MES DE EJECUCIOON DE LA PLANILLA
	SET _diafal = _id_tipo_planilla - 1;
	SET _fec_ini_faltas = SUBDATE(_fecha_tipo_planilla, INTERVAL _diafal DAY);
	SET _fec_fin_faltas = LAST_DAY(_fecha_tipo_planilla);
	
	INSERT INTO temp_ausencias_trabajador (fecha_inicio, fecha_fin,dias_ausencia) 
	SELECT fecha_inicio, fecha_fin,dias_ausencia 
	FROM p_ausencias_trabajador f
	WHERE f.id_trabajador = _id_trabajador AND f.id_tipo_ausencia = 5
	AND (f.fecha_inicio <= _fec_fin_faltas AND f.fecha_fin >= _fec_ini_faltas);
	
	SELECT COUNT(*) INTO _contadorau FROM temp_ausencias_trabajador;
	
	SET _c = 0;
	WHILE (_c < _contadorau) DO	 
	SET _c=_c + 1;
	SELECT fecha_inicio, fecha_fin,dias_ausencia INTO _fecha_inicio_aus, _fecha_fin_aus,_dias_ausencia 
	FROM temp_ausencias_trabajador WHERE contador = _c;
	
	IF _fecha_inicio_aus >= _fec_ini_faltas AND _fecha_fin_aus <= _fec_fin_faltas THEN
	SET _c_nro_faltas = _c_nro_faltas + _dias_ausencia;
	ELSE
	IF (_fecha_inicio_aus >= _fec_ini_faltas AND _fecha_inicio_aus <= _fec_fin_faltas) AND _fecha_fin_aus > _fec_fin_faltas THEN
	SET _c_nro_faltas = _c_nro_faltas + (DATEDIFF(_fec_fin_faltas,_fecha_inicio_aus) + 1);
	END IF;
	IF (_fecha_fin_aus >= _fec_ini_faltas AND _fecha_fin_aus <= _fec_fin_faltas) AND _fecha_inicio_aus < _fec_ini_faltas THEN
	SET _c_nro_faltas = _c_nro_faltas + (DATEDIFF(_fecha_fin_aus,_fec_ini_faltas) + 1);
	END IF;
	END IF;
	
	SET _fecha_inicio_aus = NULL;
	SET _fecha_fin_aus = NULL;
	END WHILE;
	-- FIN
	
	-- INGRESOS
	-- ci_sueldo_basico
	SELECT id_conceptoplanilla,descripcion_corta,monto_asignado 
