	IF _id_tipo_planilla = 1 THEN
	IF _c_essalud_vida = 0 THEN
	SELECT monto INTO _c_essalud_vida
	FROM p_conceptosplanillatrabajador WHERE id_conceptosplanilla = _id_conceptoplanilla AND
	id_trabajador = _id_trabajador AND fecha_fin >= CURDATE() AND estado = 'A' ORDER BY fecha_fin DESC LIMIT 1;
	
	END IF;
	END IF;
	-- fin _c_essalud_vida
	
	-- _cd_essalud
	
	SELECT id_conceptoplanilla,descripcion_corta,monto_asignado 
	INTO _id_conceptoplanilla,_descripcion_corta,_c_porcentage_essalud
	FROM p_conceptoplanilla WHERE descripcion_corta = 'c_porcentage_essalud';
	
	SET _cd_essalud = (_sub_total * _c_porcentage_essalud) / 100;
	
	-- fin _cd_essalud
	
	-- _c_cafae
	SELECT id_conceptoplanilla,descripcion_corta,monto_asignado 
	INTO _id_conceptoplanilla,_descripcion_corta,_c_cafae
	FROM p_conceptoplanilla WHERE descripcion_corta = 'c_cafae';
	
	IF _id_tipo_planilla = 1 THEN
	IF _c_cafae = 0 THEN
	SELECT monto INTO _c_cafae
	FROM p_conceptosplanillatrabajador WHERE id_conceptosplanilla = _id_conceptoplanilla AND
	id_trabajador = _id_trabajador AND fecha_fin >= CURDATE() AND estado = 'A' ORDER BY fecha_fin DESC LIMIT 1;
	
	END IF;
	END IF;
	
	-- fin _c_cafae
	
	-- c_adelanto
	IF _id_tipo_planilla = 1 THEN
	SELECT id_conceptoplanilla,descripcion_corta,monto_asignado 
	INTO _id_conceptoplanilla,_descripcion_corta,_c_adelanto
	FROM p_conceptoplanilla WHERE descripcion_corta = 'c_adelanto';
	
	
	IF _c_adelanto = 0 THEN
	SELECT monto INTO _c_adelanto
	FROM p_conceptosplanillatrabajador WHERE id_conceptosplanilla = _id_conceptoplanilla AND
	id_trabajador = _id_trabajador AND fecha_fin >= CURDATE() AND estado = 'A' ORDER BY fecha_fin DESC LIMIT 1;
	
	END IF;
	END IF;
	
	-- fin c_adelanto
	
	-- TARDANZAS
	SELECT id_conceptoplanilla,descripcion_corta,monto_asignado 
	INTO _id_conceptoplanilla,_descripcion_corta,_c_nro_tardanzas
	FROM p_conceptoplanilla WHERE descripcion_corta = 'c_nro_tardanzas';
	
	SELECT monto INTO _c_nro_tardanzas
	FROM p_conceptosplanillatrabajador WHERE id_conceptosplanilla = _id_conceptoplanilla AND
	id_trabajador = _id_trabajador AND fecha_fin >= CURDATE() AND estado = 'A' ORDER BY fecha_fin DESC LIMIT 1;
	
	SET _c_nro_tardanzas = _c_nro_tardanzas + _c_nro_tarde;
	
	IF _c_nro_tardanzas > 2 THEN
	SET c_3_dias_1_tar = _c_nro_tardanzas DIV 3;
	SET _c_descto_tardanzas = (_sub_total / 30) * c_3_dias_1_tar;
	--	SET _c_sueldo_neto = _c_sueldo_neto - _c_descto_tardanzas;
	END IF;
	-- FIN TARDANZAS
	IF (_c_monto_aporte_sist_pension = (NULL)) THEN SET _c_monto_aporte_sist_pension = 0; END IF;
	IF (_c_monto_prima_afp_onp = NULL) THEN SET _c_monto_prima_afp_onp = 0; END IF;
	IF (_c_monto_comision_var_afp = NULL) THEN SET _c_monto_comision_var_afp = 0; END IF;
	IF (_c_prestamos_total = NULL) THEN SET _c_prestamos_total = 0; END IF;
	IF (_c_aporte_cofap = NULL) THEN SET _c_aporte_cofap = 0; END IF;
	IF (_c_descto_sindical = NULL) THEN SET _c_descto_sindical = 0; END IF;
	IF (_c_descto_inasistencia = NULL) THEN SET _c_descto_inasistencia = 0; END IF;
	IF (_c_descto_tardanzas = NULL) THEN SET _c_descto_tardanzas = 0; END IF;
	IF (_c_quinta_categoria = NULL) THEN SET _c_quinta_categoria = 0; END IF;
	IF (_c_descto_apoyo = NULL) THEN SET _c_descto_apoyo = 0; END IF;
	IF (_c_descto_judicial = NULL) THEN SET _c_descto_judicial = 0; END IF;
	IF (_c_seguro_rimac = NULL) THEN SET _c_seguro_rimac = 0; END IF;
	IF (_c_essalud_vida = NULL) THEN SET _c_essalud_vida = 0; END IF;
	IF (_c_cafae = NULL) THEN SET _c_cafae = 0; END IF;
	IF (_c_adelanto = NULL) THEN SET _c_adelanto = 0; END IF;
	
	SET _c_tot_descuentos = _c_monto_aporte_sist_pension + _c_monto_prima_afp_onp + _c_monto_comision_var_afp; 
	SET _c_tot_descuentos = _c_tot_descuentos + _c_prestamos_total + _c_aporte_cofap + _c_descto_sindical + _c_descto_inasistencia;
	SET _c_tot_descuentos = _c_tot_descuentos  + _c_descto_judicial + _c_descto_apoyo + _c_quinta_categoria + _c_descto_tardanzas;
	SET _c_tot_descuentos = _c_tot_descuentos  + _c_seguro_rimac + _c_essalud_vida + _c_cafae + _c_adelanto;
	
	-- FIN EGRESOS
	
	
	-- CONTRIBUCIONES
	SET _c_tot_contribuciones = _cd_essalud;
	-- FIN CONTRIBUCIONES
	SET _c_sueldo_neto = _c_tot_ingresos - _c_tot_descuentos;
	
	--	if _fecha_alta > 
	
	SELECT COUNT(*) INTO _conta_proceso FROM p_planilla_trabajador
	WHERE id_trabajador = _id_trabajador AND id_tipo_planilla = _id_tipo_planilla AND
	fecha_tipo_planilla = _fecha_tipo_planilla;
	
	IF _conta_proceso = 0 THEN
	
	
	INSERT INTO p_planilla_trabajador (id_trabajador,id_tipo_planilla,fecha_tipo_planilla,fecha_proceso_planilla,
	c_sueldo_bruto,sub_total_ingresos,c_tot_ingresos,c_tot_descuentos,c_sueldo_neto,c_tot_contribuciones,
	ci_remun_basica,ci_asig_familiar,ci_sueldo_basico,
	c_res_nro_001_2012,c_bonif_famil,c_b_pers,c_transit_x_homolog,c_reunif,
	c_bonif_x_movilidad,c_res_nro_122_2012,c_agu_fiestas_patrias,c_agu_navidad,
	c_vacaciones,c_ley_29714,c_dns_276_91,	
	c_monto_aporte_sist_pension,c_porcentage_sist_pension,c_porcent_prima_sist_pension,c_monto_prima_seguro_afp,
	c_comision_var_sist_pension,c_monto_comision_var_afp,c_prestamos_total,c_aporte_cofap,c_descto_sindical,
	c_descto_inasistencia,c_nro_inasistencia,
	c_descto_judicial,c_descto_apoyo,c_quinta_categoria,c_seguro_rimac,c_essalud_vida,c_sistema_pension,
	c_porcentage_essalud,cd_essalud,c_nro_tardanzas,c_descto_tardanzas,c_cafae,c_adelanto,
	usuario_creacion,fecha_creacion) 
	VALUES (_id_trabajador,_id_tipo_planilla,_fecha_tipo_planilla,CURDATE(),
	_ci_remun_basica,_sub_total,_c_tot_ingresos,_c_tot_descuentos,_c_sueldo_neto,_c_tot_contribuciones,
	_ci_remun_basica,_ci_asig_familiar,_ci_sueldo_basico,
	_c_res_nro_001_2012,_c_bonif_famil,_c_b_pers,_c_transit_x_homolog,_c_reunif,
	_c_bonif_x_movilidad,_c_res_nro_122_2012,_c_agu_fiestas_patrias,_c_agu_navidad,
	_c_vacaciones,_c_ley_29714,_c_dns_276_91,
	_c_monto_aporte_sist_pension,_c_porcentage_afp_snp,_c_prima_afp_snp,_c_monto_prima_afp_onp,
	_c_comision_variable,_c_monto_comision_var_afp,_c_prestamos_total,_c_aporte_cofap,_c_descto_sindical,
	_c_descto_inasistencia,_c_nro_inasistencia,
	_c_descto_judicial,_c_descto_apoyo,_c_quinta_categoria,_c_seguro_rimac,_c_essalud_vida,_c_sistema_pension,
	_c_porcentage_essalud,_cd_essalud,_c_nro_tardanzas,_c_descto_tardanzas,_c_cafae,_c_adelanto,
	_usuario,NOW());
	ELSE
	UPDATE p_planilla_trabajador SET 
	--	id_trabajador=_id_trabajador,id_tipo_planilla=_id_tipo_planilla,
	--	fecha_tipo_planilla=_fecha_tipo_planilla,fecha_proceso_planilla=CURDATE(),
	c_sueldo_bruto=_ci_remun_basica,sub_total_ingresos=_sub_total,
	c_tot_ingresos=_c_tot_ingresos,c_tot_descuentos=_c_tot_descuentos,
	c_sueldo_neto=_c_sueldo_neto,c_tot_contribuciones = _c_tot_contribuciones,
	ci_remun_basica=_ci_remun_basica,
	ci_asig_familiar=_ci_asig_familiar,
	ci_sueldo_basico=_ci_sueldo_basico,
	c_res_nro_001_2012=_c_res_nro_001_2012,c_bonif_famil=_c_bonif_famil,
	c_agu_fiestas_patrias=_c_agu_fiestas_patrias,c_agu_navidad=_c_agu_navidad,
	c_b_pers=_c_b_pers,c_transit_x_homolog=_c_transit_x_homolog,c_reunif=_c_reunif,
	c_bonif_x_movilidad=_c_bonif_x_movilidad,c_res_nro_122_2012=_c_res_nro_122_2012,
	c_vacaciones=_c_vacaciones,c_ley_29714=_c_ley_29714,c_dns_276_91=_c_dns_276_91,	
	c_monto_aporte_sist_pension=_c_monto_aporte_sist_pension,
	c_porcentage_sist_pension=_c_porcentage_afp_snp,
	c_porcent_prima_sist_pension=_c_prima_afp_snp,
	c_monto_prima_seguro_afp=_c_monto_prima_afp_onp,
	c_comision_var_sist_pension=_c_comision_variable,
	c_monto_comision_var_afp=_c_monto_comision_var_afp,c_prestamos_total=_c_prestamos_total,
	c_aporte_cofap=_c_aporte_cofap,c_descto_sindical=_c_descto_sindical,
	c_descto_inasistencia=_c_descto_inasistencia, c_nro_inasistencia = _c_nro_inasistencia,
	c_descto_judicial=_c_descto_judicial,c_descto_apoyo=_c_descto_apoyo,
	c_quinta_categoria=_c_quinta_categoria,c_seguro_rimac=_c_seguro_rimac,
	c_essalud_vida=_c_essalud_vida,c_sistema_pension=_c_sistema_pension,
	c_porcentage_essalud=_c_porcentage_essalud,cd_essalud=_cd_essalud,
	c_nro_tardanzas = _c_nro_tardanzas, c_descto_tardanzas = _c_descto_tardanzas,
	c_cafae = _c_cafae,c_adelanto = _c_adelanto,
	usuario_modifica=_usuario,fecha_modifica=NOW() 
	WHERE id_trabajador = _id_trabajador AND id_tipo_planilla = _id_tipo_planilla AND
	fecha_tipo_planilla = _fecha_tipo_planilla;
	END IF;
	END WHILE;
	SELECT 1 AS result,_id_tipo_planilla;
    END */$$
