/* Procedure structure for procedure `sp_maeCargoMantenimiento` */

/*!50003 DROP PROCEDURE IF EXISTS  `sp_maeCargoMantenimiento` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_maeCargoMantenimiento`(
	_flag CHAR(1),
	_key INT,
	_cargo VARCHAR(200),
	_estado CHAR(1),
	_usuario CHAR(8)
    )
BEGIN
	DECLARE _result INT DEFAULT 1;
	DECLARE _duplicado INT DEFAULT 0;
	
	-- INSERT --
	IF _flag = '1' THEN -- nuevo
		SELECT COUNT(*) INTO _duplicado FROM m_cargo WHERE REPLACE(cargo,' ','') = REPLACE(_cargo,' ','')
									AND estado != 'E';
								
		IF _duplicado = 0 THEN
			INSERT INTO m_cargo(
				cargo,
				estado,
				usuario_creacion
			)VALUES(
				_cargo,
				_estado,
				_usuario
			);
		ELSE 
			SET _result = 2; -- duplicado
		END IF;
		SELECT _result AS result,_duplicado AS duplicado;
	END IF;
	-- UPDATE --
	IF _flag = '2' THEN
		SELECT COUNT(*) INTO _duplicado FROM m_cargo WHERE REPLACE(cargo,' ','') = REPLACE(_cargo,' ','')
									AND estado != 'E'
									AND id_cargo != _key;
		
		IF _duplicado = 0 THEN
			UPDATE m_cargo SET
				cargo = _cargo,
				estado = _estado,
				usuario_modifica = _usuario,
				fecha_modifica = NOW()
			WHERE id_cargo = _key;
		ELSE -- duplicado
			SET _result = 2; 
		END IF;
		SELECT _result AS result,_duplicado AS duplicado;
	END IF;
	-- LOGIC DELETE --
	IF _flag = '3' THEN
		UPDATE m_cargo SET estado = 'E' WHERE id_cargo = _key;
		SELECT _result AS result;
	END IF;
    END */$$
DELIMITER ;

