	_codigo VARCHAR(20),
	_epecifica VARCHAR(300),
	_idSubClasificadorDetalle INT,
	_estado CHAR(1),
	_usuario CHAR(8)
    )
BEGIN
	DECLARE _result INT DEFAULT 1;
	DECLARE _duplicado INT DEFAULT 0;
	
	-- INSERT --
	IF _flag = '1' THEN
		SELECT COUNT(*) INTO _duplicado FROM m_especifica WHERE (codigo = _codigo OR REPLACE(especifica,' ','') = REPLACE(_epecifica,' ',''))
									AND id_subclasificadordetalle = _idSubClasificadorDetalle
									AND estado != 'E';
		IF _duplicado = 0 THEN
			INSERT INTO m_especifica(
				id_subclasificadordetalle,
				codigo,
				especifica,
				estado,
				usuario_creacion
			)VALUES(
				_idSubClasificadorDetalle,
				_codigo,
				_epecifica,
				_estado,
				_usuario
			);
		ELSE -- duplicado
			SET _result = 2;
		END IF;
		SELECT _result AS result,_duplicado AS duplicado;
	END IF;
	-- UPDATE --
	IF _flag = '2' THEN
		SELECT COUNT(*) INTO _duplicado FROM m_especifica WHERE REPLACE(especifica,' ','') = REPLACE(_epecifica,' ','')
									AND id_subclasificadordetalle = _idSubClasificadorDetalle
									and id_especifica != _key
									AND estado != 'E';
		IF _duplicado = 0 THEN
			UPDATE m_especifica SET
				especifica = _epecifica,
				estado = _estado,
				usuario_modifica = _usuario,
				fecha_modifica = NOW()
			WHERE id_especifica = _key;
		ELSE -- duplicado
			SET _result = 2;
		END IF;
		SELECT _result AS result,_duplicado AS duplicado;
	END IF;
	-- LOGIC DELETE --
	IF _flag = '3' THEN
		UPDATE m_especifica SET estado = 'E' WHERE id_especifica = _key;
		SELECT _result AS result;
	END IF;
    END */$$
DELIMITER ;

/* Procedure structure for procedure `sp_maeEstructuraOrganicaMantenimiento` */

/*!50003 DROP PROCEDURE IF EXISTS  `sp_maeEstructuraOrganicaMantenimiento` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_maeEstructuraOrganicaMantenimiento`(
	_flag CHAR(1),
	_key INT,
	_area VARCHAR(200),
	_estado CHAR(1),
	_usuario CHAR(8)
    )
BEGIN
	DECLARE _result INT DEFAULT 1;
	DECLARE _duplicado INT DEFAULT 0;
	
	-- INSERT --
	IF _flag = '1' THEN -- nuevo estructura principal
		SELECT COUNT(*) INTO _duplicado FROM m_area WHERE REPLACE(`area`,' ','') = REPLACE(_area,' ','')
									AND estado != 'E'
									and dependencia = 0;
								
		IF _duplicado = 0 THEN
			INSERT INTO m_area(
				`area`,
				estado,
				usuario_creacion
			)VALUES(
				_area,
				_estado,
				_usuario
			);
		ELSE 
			SET _result = 2; -- duplicado
		END IF;
		SELECT _result AS result,_duplicado AS duplicado;
	END IF;
	-- INSERT -- 
	IF _flag = '2' THEN -- nueva estructura con dependencia
		SELECT COUNT(*) INTO _duplicado FROM m_area WHERE REPLACE(`area`,' ','') = REPLACE(_area,' ','')
									AND estado != 'E'
									AND dependencia = _key;
								
		IF _duplicado = 0 THEN
			INSERT INTO m_area(
				`area`,
				dependencia,
				estado,
				usuario_creacion
			)VALUES(
				_area,
				_key,
				_estado,
				_usuario
			);
		ELSE 
			SET _result = 2; -- duplicado
		END IF;
		SELECT _result AS result,_duplicado AS duplicado;
	end if;
	-- UPDATE --
	IF _flag = '3' THEN
		SELECT COUNT(*) INTO _duplicado FROM m_area WHERE REPLACE(`area`,' ','') = REPLACE(_area,' ','')
									AND estado != 'E'
									AND id_area != _key;
		
		IF _duplicado = 0 THEN
			UPDATE m_area SET
				`area` = _area
			WHERE id_area = _key;
		ELSE -- duplicado
			SET _result = 2; 
		END IF;
		SELECT _result AS result,_duplicado AS duplicado;
	END IF;
	-- LOGIC DELETE --
	IF _flag = '4' THEN
		UPDATE m_area SET estado = 'E' WHERE id_area = _key;
		SELECT _result AS result;
	END IF;
    END */$$
DELIMITER ;

