/* Procedure structure for procedure `sp_maeMaestrosConsultas` */

/*!50003 DROP PROCEDURE IF EXISTS  `sp_maeMaestrosConsultas` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_maeMaestrosConsultas`(
	_flag int,
	_criterio varchar(50)
    )
BEGIN	
	-- BUSCAR CLASIFICADOR PARA EDITAR --
	if _flag = 1 then 
		select 
			codigo,
			clasificador,
			estado,
			tipo
		from m_clasificador 
		where id_clasificador = _criterio;
	end if;
	-- TODOS LOS CLASIFICADORES --
	if _flag = 2 then
		select
			id_clasificador,
			codigo,
			clasificador
		from m_clasificador 
		where estado = 'A'; 
	end if;
	-- TODOS LOS SUB CLASIFICADORES --
	IF _flag = 3 THEN
		SELECT
			id_subclasificador,
			codigo,
			subclasificador
		FROM m_subclasificador 
		WHERE estado = 'A'; 
	END IF;
	-- TODOS LOS SUB CLASIFICADORES DETALLE--
	IF _flag = 5 THEN
		SELECT
			id_subclasificadordetalle,
			codigo,
			subclasificadordetalle
		FROM m_subclasificadordetalle 
		WHERE estado != 'E'
		and if(_criterio = '',true,id_clasificadordetalle = _criterio); 
	END IF;
	
	-- BUSCAR SUB CLASIFICADOR PARA EDITAR --
	IF _flag = 6 THEN
		SELECT
			id_clasificador,
			codigo,
			subclasificador,
			estado
		FROM m_subclasificador 
		WHERE id_subclasificador = _criterio; 
	END IF;
	-- BUSCAR CLASIFICADOR DETALLE PARA EDITAR --
	IF _flag = 7 THEN
		SELECT
			id_subclasificador,
			codigo,
			clasificadordetalle,
			estado
		FROM m_clasificadordetalle 
		WHERE id_clasificadordetalle = _criterio; 
	END IF;
	-- CLASIFICADOR DETALLE DE UN SUB CLASIFICADOR --
	IF _flag = 8 THEN
		SELECT
			id_clasificadordetalle,
			codigo,
			clasificadordetalle
		FROM m_clasificadordetalle 
		WHERE if(_criterio = '',true,id_subclasificador = _criterio)
		and estado != 'E'; 
	END IF;
	-- BUSCAR SUB CLASIFICADOR DETALLE PARA EDITAR --
	IF _flag = 9 THEN
		SELECT
			scd.id_clasificadordetalle,
			scd.codigo,
			scd.subclasificadordetalle,
			scd.estado,
			cd.id_clasificadordetalle,
			sc.id_subclasificador
		FROM m_subclasificadordetalle scd
		inner join m_clasificadordetalle cd on cd.id_clasificadordetalle = scd.id_clasificadordetalle
		inner join m_subclasificador sc on sc.id_subclasificador = cd.id_subclasificador
		WHERE scd.id_subclasificadordetalle = _criterio; 
	END IF;
	-- BUSCAR ESPECIFICA PARA EDITAR --
	IF _flag = 10 THEN
		SELECT  
			CONCAT(sc.codigo,' ',sc.subclasificador)AS subclasificador,
			CONCAT(scd.codigo,' ',scd.subclasificadordetalle)AS subclasificadordetalle,
			CONCAT(cd.codigo,' ',cd.clasificadordetalle)AS clasificadordetalle,	
			sc.id_clasificador,
			e.codigo,
			e.especifica,
			e.estado
		FROM m_especifica e
		INNER JOIN m_subclasificadordetalle scd ON scd.id_subclasificadordetalle = e.id_subclasificadordetalle
		INNER JOIN m_clasificadordetalle cd ON cd.id_clasificadordetalle = scd.id_clasificadordetalle
		INNER JOIN m_subclasificador sc ON sc.id_subclasificador = cd.id_subclasificador
		WHERE e.id_especifica = _criterio;
	END IF;
	-- BUSCAR BANCO PARA EDITAR --
	IF _flag = 11 THEN
		SELECT  
			banco,
			estado
		FROM m_banco
		WHERE id_banco = _criterio;
	END IF;
	-- TODOS LOS BANCOS ACTIVOS--
	IF _flag = 12 THEN
		SELECT  
			id_banco,
			banco
		FROM m_banco
		WHERE estado = 'A';
	END IF;
	-- BUSCAR CUENTA CORRIENTE PARA EDITAR --
	IF _flag = 13 THEN
		SELECT  
			id_banco,
			cuentacorriente,
			estado
		FROM m_cuentacorriente
		WHERE id_cuentacorriente = _criterio;
	END IF;
	-- BUSCAR TIPO DOC IDENTIDAD PARA EDITAR --
	IF _flag = 14 THEN
		SELECT  
			tipodocumentoidentidad,
			estado
		FROM m_tipodocumentoidentidad
		WHERE id_tipodocumentoidentidad = _criterio;
	END IF;
	-- BUSCAR TIPO DE PAGO PARA EDITAR --
	IF _flag = 15 THEN
		SELECT  
			tipopago,
			estado
		FROM m_tipopago
		WHERE id_tipopago = _criterio;
	END IF;
	-- BUSCAR TIPO DE CONTRATO PARA EDITAR --
	IF _flag = 16 THEN
		SELECT  
			id_tipocontrato,
			contrato,
			contenido,
			estado
		FROM m_tipocontrato
		WHERE id_tipocontrato = _criterio;
	END IF;
	-- BUSCAR CARGO PARA EDITAR --
	IF _flag = 17 THEN
		SELECT  
			id_cargo,
			cargo,
			estado
		FROM m_cargo
		WHERE id_cargo = _criterio;
	END IF;
	-- BUSCAR AREA PARA EDITAR --
	IF _flag = 18 THEN
		SELECT  
			`area`,
			estado
		FROM m_area
		WHERE id_area = _criterio;
	END IF;
	-- BUSCAR CATEGORIA DE TRABAJADOR PARA EDITAR --
	IF _flag = 19 THEN
		SELECT  
			categoriatrabajador,
			sicla,
			estado
		FROM m_categoriatrabjador
		WHERE id_categoriatrabajador = _criterio;
	END IF;
	-- BUSCAR CONCEPTO DE PLANILLA PARA EDITAR --
	IF _flag = 20 THEN
		SELECT  
			conceptoplanilla,
			descripcion_corta,
			clasificacion,
			estado,
			monto_asignado,
			id_concepto_totalizador
		FROM p_conceptoplanilla
		WHERE id_conceptoplanilla = _criterio;
	END IF;
	-- PARA GENERAR COMBO CLASIFICADOR EN FORM CLASIFICADOR 5 NIVELES --
	IF _flag = 21 THEN
		SELECT
			c.id_clasificador,
			c.codigo AS codigoclasificador,
			c.clasificador,
			sc.id_subclasificador,
			sc.codigo AS codigosubclasificador,
			sc.subclasificador,
			cd.id_clasificadordetalle,
			cd.codigo AS codigoclasificadordetalle,
			cd.clasificadordetalle,
			scd.id_subclasificadordetalle,
			scd.codigo AS codigosubclasificadordetalle,
			scd.subclasificadordetalle,
			e.id_especifica,
			e.codigo AS codigoespecifica,
			e.especifica
		FROM m_especifica e 
		INNER JOIN m_subclasificadordetalle scd ON scd.id_subclasificadordetalle = e.id_subclasificadordetalle
		INNER JOIN m_clasificadordetalle cd ON cd.id_clasificadordetalle = scd.id_clasificadordetalle
		INNER JOIN m_subclasificador sc ON sc.id_subclasificador = cd.id_subclasificador
		INNER JOIN m_clasificador c ON c.id_clasificador = sc.id_clasificador
		WHERE e.estado = 'A'
		ORDER BY 2,5,8,11,14;
	END IF;
	-- BUSCAR CLASIFICADOR DERIVADO PARA EDITAR --
	IF _flag = 22 THEN
		SELECT  
			id_especifica,
			codigo,
			clasificadorderivado,
			estado
		FROM m_clasificadorderivado
		WHERE id_clasificadorderivado = _criterio;
	END IF;
	-- PARA GENERAR COMBO CLASIFICADOR EN FORM CLASIFICADOR 6 NIVELES --
	IF _flag = 23 THEN
		SELECT
			c.id_clasificador,
			c.codigo AS codigoclasificador,
			c.clasificador,
			sc.id_subclasificador,
			sc.codigo AS codigosubclasificador,
			sc.subclasificador,
			cd.id_clasificadordetalle,
			cd.codigo AS codigoclasificadordetalle,
			cd.clasificadordetalle,
			scd.id_subclasificadordetalle,
			scd.codigo AS codigosubclasificadordetalle,
			scd.subclasificadordetalle,
			e.id_especifica,
			e.codigo AS codigoespecifica,
			e.especifica,
			d.id_clasificadorderivado,
			d.codigo AS codigoderivado,
			d.clasificadorderivado
		FROM m_clasificadorderivado d
		INNER JOIN m_especifica e ON e.id_especifica = d.id_especifica
		INNER JOIN m_subclasificadordetalle scd ON scd.id_subclasificadordetalle = e.id_subclasificadordetalle
		INNER JOIN m_clasificadordetalle cd ON cd.id_clasificadordetalle = scd.id_clasificadordetalle
		INNER JOIN m_subclasificador sc ON sc.id_subclasificador = cd.id_subclasificador
		INNER JOIN m_clasificador c ON c.id_clasificador = sc.id_clasificador
		WHERE e.estado = 'A'
		ORDER BY 2,5,8,11,14,17;
	END IF;
	-- BUSCAR ACTIVIDAD PARA EDITAR --
	IF _flag = 24 THEN
		SELECT  
			id_actividad,
			id_clasificadorderivado,
			actividad,
			estado
		FROM m_actividad
		WHERE id_actividad = _criterio;
	END IF;
	-- BUSCAR TIPO VINCULO FAMILIAR PARA EDITAR --
	IF _flag = 25 THEN
		SELECT  
			vinculofamiliar,
			estado
		FROM m_tipovinculofamiliar
		WHERE id_tipovinculofamiliar = _criterio;
	END IF;
	-- LISTADO DE AREAS PARA ORGANIGRAMA  --
	IF _flag = 26 THEN
		SELECT
			id_area,
			`area`,
			IF(dependencia = 0,NULL,dependencia)AS dependencia
		FROM m_area
		where estado = 'A';
	END IF;
	IF _flag = 27 THEN
		select 
			id_conceptoplanilla,
			conceptoplanilla
		from p_conceptoplanilla where tipo_concepto = 2 and estado = 'A';
	END IF;
    END */$$
DELIMITER ;

/* Procedure structure for procedure `sp_maePlanillaTrabajadorConsultar` */

/*!50003 DROP PROCEDURE IF EXISTS  `sp_maePlanillaTrabajadorConsultar` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_maePlanillaTrabajadorConsultar`(
	_flag CHAR(1),
	_id_tipo_proceso_planilla INT, -- id que contine id_tipo_planilla, fecha_tipo_planilla...
	_usuario CHAR(8)
    )
BEGIN
		-- CALL sp_maeCalculaPlanillaTrabajador(1,1,CURDATE(),'FCL')
	DECLARE _conta_proceso INT;
	DECLARE _id_tipo_planilla INT;	-- 1: planilla mensual
	DECLARE _fecha_tipo_planilla DATE;
	DECLARE _msm VARCHAR(20);
	
	
	-- DATOS DEL PROCESO PLANILLA
		SELECT id_tipo_planilla,fecha_tipo_proceso_planilla 
		INTO _id_tipo_planilla,_fecha_tipo_planilla
		FROM m_tipo_proceso_planilla WHERE id_tipo_proceso_planilla = _id_tipo_proceso_planilla;
	-- fin DATOS DEL PROCESO PLANILLA
	
	SELECT COUNT(*) INTO _conta_proceso FROM p_planilla_trabajador
		WHERE id_tipo_planilla = _id_tipo_planilla AND
		fecha_tipo_planilla = _fecha_tipo_planilla;
	
	IF _conta_proceso = 0 THEN
		
		SELECT 'No existe registros...!';
		
	ELSE
		SELECT p.id_trabajador,pe.nombrecompleto,p.fecha_proceso_planilla,p.ci_remun_basica,p.c_reunif,p.c_transit_x_homolog,
		p.c_b_pers,p.c_bonif_famil,p.c_res_nro_001_2012,p.c_res_nro_122_2012,p.c_bonif_x_movilidad,p.ci_asig_familiar,
		p.sub_total_ingresos AS 'SUB TOTAL',p.c_vacaciones,p.c_ley_29714,p.c_dns_276_91,p.c_tot_ingresos AS 'TOTAL PERCIBOS',
		p.c_sistema_pension AS 'id Sist.Pension', s.sistemapension AS 'Sistema Pension',p.c_monto_aporte_sist_pension,
		p.c_monto_prima_seguro_afp, p.c_monto_comision_var_afp, p.c_prestamo_cmac_sull, p.c_prestamo_cmac_paita,
		p.c_prestamo_cofap,p.c_aporte_cofap,p.c_descto_sindical,p.c_descto_inasistencia,p.c_descto_judicial,
		p.c_descto_apoyo,p.c_quinta_categoria,p.c_seguro_rimac,p.c_essalud_vida,p.c_tot_descuentos AS 'TOTAL DESCUENTOS',
		p.c_sueldo_neto AS 'NETO A PERCIBIR', p.cd_essalud
		
		FROM p_planilla_trabajador p, p_trabajador t, mae_persona pe, m_sistemapension s
		WHERE p.id_trabajador = t.id_trabajador AND t.id_persona = pe.id_persona AND 
		p.c_sistema_pension = s.id_sistemapension AND
		id_tipo_planilla = _id_tipo_planilla AND
		fecha_tipo_planilla = _fecha_tipo_planilla;
	END IF;
    END */$$
DELIMITER ;

/* Procedure structure for procedure `sp_maeSubClasificadorDetalleGrid` */

/*!50003 DROP PROCEDURE IF EXISTS  `sp_maeSubClasificadorDetalleGrid` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_maeSubClasificadorDetalleGrid`(
	_iDisplayStart INT,
        _iDisplayLength INT, 
        _iOrderCol VARCHAR(200),
        _sSearch VARCHAR(200),
        _sFilterCols VARCHAR(300)
    )
BEGIN
	DECLARE orderby VARCHAR(300) DEFAULT '';
	DECLARE isearch VARCHAR(300) DEFAULT '';
	DECLARE filterCols VARCHAR(300) DEFAULT '';
	
	-- IF _sSearch <> '' THEN
		-- SET isearch = CONCAT(' AND m.modulo LIKE "%',_sSearch,'%" ');
	-- END IF;
	IF _sFilterCols <> '' THEN
		SET filterCols = REPLACE(_sFilterCols,'&quot;','"');
		SET filterCols = REPLACE(filterCols,'*','%');
	END IF;
	IF _iOrderCol <> '' THEN
		SET orderby = CONCAT(' ORDER BY ',_iOrderCol);
	END IF;
	
	SET @sentencia = CONCAT('
	SELECT
		COUNT(*) INTO @countx
	FROM
	(
		SELECT
			scd.id_subclasificadordetalle,
			scd.codigo AS codigoscd,
			scd.subclasificadordetalle,
			scd.estado AS estadoscd,
			cd.clasificadordetalle,
			sc.subclasificador,
			c.clasificador
		FROM m_subclasificadordetalle scd
		INNER JOIN m_clasificadordetalle cd ON cd.id_clasificadordetalle = scd.id_clasificadordetalle
		INNER JOIN m_subclasificador sc ON sc.id_subclasificador = cd.id_subclasificador
		INNER JOIN m_clasificador c ON c.id_clasificador = sc.id_clasificador
	) D
	WHERE estadoscd != "E"
	',isearch,' ',filterCols,' ;');
	
	PREPARE consulta FROM @sentencia;
	EXECUTE consulta;
	DEALLOCATE PREPARE consulta;
	SET @sentencia = NULL;
	
	SET @sentencia = CONCAT('
	SELECT
		total,
		id_subclasificadordetalle,
		codigoscd,
		subclasificadordetalle,
		estadoscd,
		clasificadordetalle,
		subclasificador,
		clasificador
	FROM
	(
		SELECT
			@countx as total,
			scd.id_subclasificadordetalle,
			scd.codigo AS codigoscd,
			scd.subclasificadordetalle,
			scd.estado AS estadoscd,
			cd.clasificadordetalle,
			sc.subclasificador,
			c.clasificador
		FROM m_subclasificadordetalle scd
		INNER JOIN m_clasificadordetalle cd ON cd.id_clasificadordetalle = scd.id_clasificadordetalle
		INNER JOIN m_subclasificador sc ON sc.id_subclasificador = cd.id_subclasificador
		INNER JOIN m_clasificador c ON c.id_clasificador = sc.id_clasificador
	) D
	WHERE estadoscd != "E"
	',isearch,' ',filterCols,' ',orderby,' LIMIT ',_iDisplayStart,',',_iDisplayLength,'; ');
		
	PREPARE consulta FROM @sentencia;
	EXECUTE consulta;
	DEALLOCATE PREPARE consulta;
	SET @sentencia = NULL;
    END */$$
DELIMITER ;

/* Procedure structure for procedure `sp_maeSubClasificadorDetalleMantenimiento` */

/*!50003 DROP PROCEDURE IF EXISTS  `sp_maeSubClasificadorDetalleMantenimiento` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_maeSubClasificadorDetalleMantenimiento`(
	_flag CHAR(1),
	_key INT,
	_codigo VARCHAR(20),
	_subclasificadordetalle VARCHAR(300),
