	WHERE estado != "E" ORDER BY fecha_tipo_proceso_planilla DESC
	',isearch,' ',filterCols,' ',orderby,' LIMIT ',_iDisplayStart,',',_iDisplayLength,'; ');
		
	PREPARE consulta FROM @sentencia;
	EXECUTE consulta;
	DEALLOCATE PREPARE consulta;
	SET @sentencia = NULL;
    END */$$
DELIMITER ;

/* Procedure structure for procedure `sp_maeTipoProcesoPlanillaConsultas` */

