/* Procedure structure for procedure `sp_maeTipoProcesoPlanillaMantenimiento` */

/*!50003 DROP PROCEDURE IF EXISTS  `sp_maeTipoProcesoPlanillaMantenimiento` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_maeTipoProcesoPlanillaMantenimiento`(
	_flag CHAR(1),
	_key INT,
	_id_tipo_planilla BIGINT(20),
	_num_mes_tipo_procesop INT(2),
	_ano_tipo_procesop INT(4),
	_usuario CHAR(8)
    )
BEGIN
	DECLARE _result INT DEFAULT 1;
	DECLARE _duplicado INT DEFAULT 0;
	
	DECLARE _tipo_proceso_planilla VARCHAR(100);
	DECLARE _fecha_tipo_proceso_planilla VARCHAR(10);
	DECLARE _nom_mes_tipo_procesop CHAR(20);
	DECLARE _situacion CHAR(1);
	
	-- ---------------------------------
	IF _num_mes_tipo_procesop = 1 THEN 
			SET _nom_mes_tipo_procesop = "Enero";
		END IF;
		IF _num_mes_tipo_procesop = 2 THEN 
			SET _nom_mes_tipo_procesop = "Febrero";
		END IF;
		IF _num_mes_tipo_procesop = 3 THEN 
			SET _nom_mes_tipo_procesop = "Marzo";
		END IF;
		IF _num_mes_tipo_procesop = 4 THEN 
			SET _nom_mes_tipo_procesop = "Abril";
		END IF;
		IF _num_mes_tipo_procesop = 5 THEN 
			SET _nom_mes_tipo_procesop = "Mayo";
		END IF;
		IF _num_mes_tipo_procesop = 6 THEN 
			SET _nom_mes_tipo_procesop = "Junio";
		END IF;
		IF _num_mes_tipo_procesop = 7 THEN 
			SET _nom_mes_tipo_procesop = "Julio";
		END IF;
		IF _num_mes_tipo_procesop = 8 THEN 
			SET _nom_mes_tipo_procesop = "Agosto";
		END IF;
		IF _num_mes_tipo_procesop = 9 THEN 
			SET _nom_mes_tipo_procesop = "Setiembre";
		END IF;
		IF _num_mes_tipo_procesop = 10 THEN 
			SET _nom_mes_tipo_procesop = "Octubre";
		END IF;
		IF _num_mes_tipo_procesop = 11 THEN 
			SET _nom_mes_tipo_procesop = "Noviembre";
		END IF;
		IF _num_mes_tipo_procesop = 12 THEN 
			SET _nom_mes_tipo_procesop = "Diciembre";
		END IF;
		
		SELECT tipoplanilla INTO _tipo_proceso_planilla FROM m_tipoplanilla WHERE id_tipoplanilla = _id_tipo_planilla;
				
		SET _tipo_proceso_planilla = CONCAT(_tipo_proceso_planilla,", ",_nom_mes_tipo_procesop," - ",_ano_tipo_procesop);
		
		SET _fecha_tipo_proceso_planilla = CONCAT(_ano_tipo_procesop,"-",_num_mes_tipo_procesop,"-",_id_tipo_planilla);
		
	-- ---------------------------------
	
	-- INSERT --
	IF _flag = '1' THEN -- nuevo
		
		
		
		SELECT COUNT(*) INTO _duplicado FROM m_tipo_proceso_planilla WHERE REPLACE(tipo_proceso_planilla,' ','') = REPLACE(_tipo_proceso_planilla,' ','')
		AND estado != 'E';
		
		IF _duplicado = 0 THEN
			INSERT INTO m_tipo_proceso_planilla(
				id_tipo_planilla,
				tipo_proceso_planilla,
				fecha_tipo_proceso_planilla,
				num_mes_tipo_procesop,
				nom_mes_tipo_procesop,
				ano_tipo_procesop,
				usuario,
				fecha_sistema
			)VALUES(
				_id_tipo_planilla,
				_tipo_proceso_planilla,
				_fecha_tipo_proceso_planilla,
				_num_mes_tipo_procesop,
				_nom_mes_tipo_procesop,
				_ano_tipo_procesop,
				_usuario,
				NOW()
			);
		ELSE 
			SET _result = 2; -- duplicado
		END IF;
		SELECT _result AS result,_duplicado AS duplicado;
	END IF;
	-- UPDATE --
	IF _flag = '2' THEN
		SELECT COUNT(*) INTO _duplicado FROM m_tipo_proceso_planilla WHERE REPLACE(tipo_proceso_planilla,' ','') = REPLACE(_tipo_proceso_planilla,' ','')
									AND estado != 'E'
									AND id_tipo_proceso_planilla != _key;
		
		
		IF _duplicado = 0 THEN
			UPDATE m_tipo_proceso_planilla SET
				id_tipo_planilla = _id_tipo_planilla,
				tipo_proceso_planilla = _tipo_proceso_planilla,
				fecha_tipo_proceso_planilla = _fecha_tipo_proceso_planilla,
				num_mes_tipo_procesop = _num_mes_tipo_procesop,
				nom_mes_tipo_procesop = _nom_mes_tipo_procesop,
				ano_tipo_procesop = _ano_tipo_procesop,
				usuario = _usuario,
				fecha_sistema = NOW()
			WHERE id_tipo_proceso_planilla = _key;
		ELSE -- duplicado
			SET _result = 2; 
		END IF;
		SELECT _result AS result,_duplicado AS duplicado;
	END IF;
	-- LOGIC DELETE --
	IF _flag = '3' THEN
		UPDATE m_tipo_proceso_planilla SET estado = 'E' WHERE id_tipo_proceso_planilla = _key;
		SELECT _result AS result;
	END IF;
    END */$$
DELIMITER ;

