		WHERE m.id_tipo_vac = a.id_tipo_ausencia AND m.id_trabajador = "',_idTrabajador,'"
		',isearch,' ',filterCols,' ',orderby,' 
		LIMIT ',_iDisplayStart,',',_iDisplayLength,'; ');
		
	END IF;
	
	IF _flag = 2 THEN
		
		SET @sentencia = CONCAT('
		SELECT 
