		FROM p_ausencias_trabajador m, m_tipo_ausencia a
		WHERE m.id_tipo_ausencia = a.id_tipo_ausencia AND m.estado != "E" AND m.id_trabajador = "',_idTrabajador,'"
		',isearch,' ',filterCols,' ',orderby,' 
		LIMIT ',_iDisplayStart,',',_iDisplayLength,'; ');
	END IF;
	
	-- _criterio
	PREPARE consulta FROM @sentencia;
	EXECUTE consulta;
	DEALLOCATE PREPARE consulta;
	SET @sentencia = NULL;
	
	
    END */$$
