/* Procedure structure for procedure `sp_maeVacMovTrabMantenimiento` */

/*!50003 DROP PROCEDURE IF EXISTS  `sp_maeVacMovTrabMantenimiento` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_maeVacMovTrabMantenimiento`(
	_flag INT,
	_key INT,  -- id_ausencia_trabajador
	_id_trabajador BIGINT(20),
	_id_tipo_ausencia BIGINT(20),
--	_id_ausencias_trabajador BIGINT(20),
	_fecha_inicio DATE,
	_fecha_fin DATE,
	_dias_ausencia INTEGER(4),
	_fec_proc_planilla DATE,
	_comentario VARCHAR(250),
	_estado CHAR(1),
	_usuario CHAR(8)
    )
BEGIN
	DECLARE _result INT DEFAULT 1;
	DECLARE _duplicado INT DEFAULT 0;
	DECLARE _solapamiento INT DEFAULT 0;
	DECLARE _periodo_vac INT DEFAULT 0;
	DECLARE _id_vac_saldo INT DEFAULT 0;
	DECLARE _id_vac_movimiento INT DEFAULT 0;
	DECLARE _dias_ausencia_ant INT DEFAULT 0;
	
	-- INSERT --
	IF _flag = 1 THEN -- nuevo
		SELECT COUNT(*) INTO _solapamiento FROM p_ausencias_trabajador WHERE id_trabajador = _id_trabajador
									AND fecha_inicio <= _fecha_fin
									AND fecha_fin >= _fecha_inicio;
																	
		IF _solapamiento = 0 THEN
		
			INSERT INTO p_ausencias_trabajador(
				id_trabajador,
				id_tipo_ausencia,
				fecha_inicio,
				fecha_fin,
				dias_ausencia,
				fec_proc_planilla,
				comentario,
				-- ultimo_proceso,
				usuario
			)VALUES(
				_id_trabajador,
				_id_tipo_ausencia,
				_fecha_inicio,
				_fecha_fin,
				_dias_ausencia,
				_fec_proc_planilla ,
				_comentario,
				-- _estado,
				_usuario
			);
			
			IF _id_tipo_ausencia = 1 OR _id_tipo_ausencia = 2 OR _id_tipo_ausencia = 3 THEN
				
				-- ACTUALIZO SALDO DE VACACIONES
				SELECT id_vac_saldo, periodo_vac INTO _id_vac_saldo, _periodo_vac FROM p_vac_saldo WHERE id_trabajador = _id_trabajador 
											AND saldo_vac > 0 OR dias_truncos > 0 ORDER BY periodo_vac ASC LIMIT 1;
				-- GOCE DE VACACIONES
				IF _id_tipo_ausencia = 1 THEN
					UPDATE p_vac_saldo SET
					dias_gozados = dias_gozados + _dias_ausencia,
					saldo_vac = saldo_vac - _dias_ausencia
					WHERE id_vac_saldo = _id_vac_saldo;
				END IF;
				-- COMPENSACION DE VACACIONES
				IF _id_tipo_ausencia = 2 THEN
					UPDATE p_vac_saldo SET
					dias_compensados = dias_compensados + _dias_ausencia,
					saldo_vac = saldo_vac - _dias_ausencia
					WHERE id_vac_saldo = _id_vac_saldo;
				END IF;
				-- INDEMNIZACION DE VACACIONES
				IF _id_tipo_ausencia = 3 THEN
					UPDATE p_vac_saldo SET
					dias_indemnizados = dias_indemnizados + _dias_ausencia,
					saldo_vac = saldo_vac - _dias_ausencia
					WHERE id_vac_saldo = _id_vac_saldo;
				END IF;
				
				-- INGRESO MOVIEMTO DE VACACIONES
				INSERT INTO p_vac_movimientos(
					id_vac_saldo,
					id_trabajador,
					periodo_vac,
					fecha_inicio,
					fecha_fin,
					id_tipo_vac,
					dias_vac,
					comentario,
					-- ultimo_proceso,
					usuario
				)VALUES(
					_id_vac_saldo,
					_id_trabajador,
					_periodo_vac,
					_fecha_inicio,
					_fecha_fin,
					_id_tipo_ausencia,
					_dias_ausencia,
					_comentario,
					-- _estado,
					_usuario
				);
			END IF;
		ELSE 
			SET _result = 2; -- duplicado
		END IF;
		SELECT _result AS result,_solapamiento AS duplicado;
	END IF;
	
	-- UPDATE --
	IF _flag = 2 THEN
		-- pasar el _id_ausencias_trabajador y todos los datos;
		SELECT dias_ausencia, id_tipo_ausencia, id_trabajador INTO _dias_ausencia_ant, _id_tipo_ausencia, _id_trabajador
				FROM p_ausencias_trabajador
				WHERE id_ausencias_trabajador = _key;
				
		-- verifica que las ausencia modificada no se cruce con otra
		SELECT COUNT(*) INTO _solapamiento FROM p_ausencias_trabajador WHERE id_trabajador = _id_trabajador
		AND fecha_inicio <= _fecha_fin AND fecha_fin >= _fecha_inicio AND id_ausencias_trabajador != _key;
																	
		IF _solapamiento = 0 THEN
				
			UPDATE p_ausencias_trabajador SET
				fecha_inicio = _fecha_inicio,
				fecha_fin = _fecha_fin,
				dias_ausencia = _dias_ausencia,
				fec_proc_planilla = _fec_proc_planilla,
				comentario = _comentario,
				ultimo_proceso = NOW(),
				usuario = _usuario
				WHERE id_ausencias_trabajador = _key;
							
			IF _id_tipo_ausencia = 1 OR _id_tipo_ausencia = 2 OR _id_tipo_ausencia = 3 THEN
			
				-- obtengo el ACTUALIZO SALDO DE VACACIONES
				SELECT id_vac_saldo, id_vac_movimiento INTO _id_vac_saldo, _id_vac_movimiento FROM p_vac_movimientos
				WHERE id_trabajador = _id_trabajador AND (fecha_inicio = _fecha_inicio AND fecha_fin =_fecha_fin)
				ORDER BY periodo_vac ASC LIMIT 1;
				
				-- GOCE DE VACACIONES
				IF _id_tipo_ausencia = 1 THEN
					UPDATE p_vac_saldo SET
					dias_gozados = (dias_gozados - _dias_ausencia_ant) + _dias_ausencia,
					saldo_vac = (saldo_vac + _dias_ausencia_ant) - _dias_ausencia,
					ultimo_proceso = NOW(),
					usuario = _usuario
					WHERE id_vac_saldo = _id_vac_saldo;
				END IF;
				-- COMPENSACION DE VACACIONES
				IF _id_tipo_ausencia = 2 THEN
					UPDATE p_vac_saldo SET
					dias_compensados = (dias_compensados - _dias_ausencia_ant) + _dias_ausencia,
					saldo_vac = (saldo_vac + _dias_ausencia_ant) - _dias_ausencia,
					ultimo_proceso = NOW(),
					usuario = _usuario
					WHERE id_vac_saldo = _id_vac_saldo;
				END IF;
				-- INDEMNIZACION DE VACACIONES
				IF _id_tipo_ausencia = 3 THEN
					UPDATE p_vac_saldo SET
					dias_indemnizados = (dias_indemnizados - _dias_ausencia_ant) + _dias_ausencia,
					saldo_vac = (saldo_vac + _dias_ausencia_ant) - _dias_ausencia,
					ultimo_proceso = NOW(),
					usuario = _usuario
					WHERE id_vac_saldo = _id_vac_saldo;
				END IF;
				
				-- INGRESO MOVIEMTO DE VACACIONES
				UPDATE p_vac_movimientos SET
				fecha_inicio = _fecha_inicio,
				fecha_fin = _fecha_fin,
				dias_vac = _dias_ausencia,
				comentario = _comentario,
				ultimo_proceso = NOW(),
				usuario = _usuario
				WHERE id_vac_movimiento = _id_vac_movimiento;
				
				
			END IF;
		ELSE 
			SET _result = 2; -- duplicado
		END IF;
		SELECT _result AS result,_solapamiento AS duplicado;
	END IF;
	
	-- LOGIC DELETE --
	IF _flag = 3 THEN
	
		-- pasar el _id_ausencias_trabajador;
		
		SELECT id_tipo_ausencia, fecha_inicio, fecha_fin, dias_ausencia, id_trabajador INTO _id_tipo_ausencia, _fecha_inicio, _fecha_fin, _dias_ausencia, _id_trabajador
			FROM p_ausencias_trabajador
			WHERE id_ausencias_trabajador = _key;
			
		UPDATE p_ausencias_trabajador SET estado = 'E' WHERE id_ausencias_trabajador = _key;
		
		IF _id_tipo_ausencia = 1 OR _id_tipo_ausencia = 2 OR _id_tipo_ausencia = 3 THEN
		
			-- obtengo el ACTUALIZO SALDO DE VACACIONES
			SELECT id_vac_saldo, id_vac_movimiento INTO _id_vac_saldo, _id_vac_movimiento FROM p_vac_movimientos
			WHERE id_trabajador = _id_trabajador AND (fecha_inicio = _fecha_inicio AND fecha_fin =_fecha_fin);
		--	ORDER BY periodo_vac ASC LIMIT 1;
										
			-- GOCE DE VACACIONES
			IF _id_tipo_ausencia = 1 THEN
				UPDATE p_vac_saldo SET
				dias_gozados = dias_gozados - _dias_ausencia,
				saldo_vac = saldo_vac + _dias_ausencia,
				ultimo_proceso = NOW(),
				usuario = _usuario
				WHERE id_vac_saldo = _id_vac_saldo;
			END IF;
			-- COMPENSACION DE VACACIONES
			IF _id_tipo_ausencia = 2 THEN
				UPDATE p_vac_saldo SET
				dias_compensados = dias_compensados - _dias_ausencia,
				saldo_vac = saldo_vac + _dias_ausencia,
				ultimo_proceso = NOW(),
				usuario = _usuario
				WHERE id_vac_saldo = _id_vac_saldo;
			END IF;
			-- INDEMNIZACION DE VACACIONES
			IF _id_tipo_ausencia = 3 THEN
				UPDATE p_vac_saldo SET
				dias_indemnizados = dias_indemnizados - _dias_ausencia,
				saldo_vac = saldo_vac + _dias_ausencia,
				ultimo_proceso = NOW(),
				usuario = _usuario
				WHERE id_vac_saldo = _id_vac_saldo;
			END IF;
			
			UPDATE p_vac_movimientos SET 
			estado = 'E',
			ultimo_proceso = NOW(),
			usuario = _usuario 
			WHERE id_vac_movimiento = _id_vac_movimiento;
			
		END IF;
		SELECT _result AS result, _id_tipo_ausencia, _id_vac_saldo;
	END IF;
    END */$$
DELIMITER ;

/* Procedure structure for procedure `sp_maeVacRecordTrabMantenimiento` */

/*!50003 DROP PROCEDURE IF EXISTS  `sp_maeVacRecordTrabMantenimiento` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_maeVacRecordTrabMantenimiento`(
	_usuario CHAR(8)
    )
BEGIN
	DECLARE _id_trabajador BIGINT(20);
	DECLARE _id_persona BIGINT(20);
	DECLARE _fecha_alta DATE;
	DECLARE _id_vac_saldo BIGINT(20);
	DECLARE _fecha_inicio DATE;
	DECLARE _fecha_fin DATE;
	DECLARE _periodo_vac INT;
	DECLARE _diasTrabajados INT;
	DECLARE _m_ini INT;
	DECLARE _m_hoy INT;
	DECLARE _mes INT;
	DECLARE _a_ini INT;
	DECLARE _a_hoy INT;
	DECLARE _ano INT;
	DECLARE _d_ini INT;
	DECLARE _d_hoy INT;
	DECLARE _dia INT;
	DECLARE _contadorSaldoTrab INT;
	DECLARE _contadorCreaSaldo INT;
	DECLARE _insertarSaldo INT;
	DECLARE _truncos DOUBLE(5,2);
	DECLARE current_status CHAR (1);
	DECLARE _fecha_evalua DATE;
	DECLARE _dia_evalua INT;
	DECLARE _ndia_fs INT DEFAULT 0;
	DECLARE fin INT DEFAULT 0;
	DECLARE CursorTrabajadores CURSOR FOR SELECT id_trabajador,id_persona,fecha_alta FROM p_trabajador 
							WHERE estado >= 'A' AND fecha_alta <> '0000-00-00' ;
							-- fecha_baja >= CURDATE() 
	DECLARE CONTINUE HANDLER FOR  NOT FOUND SET fin=1;
	OPEN CursorTrabajadores;
	ciclo_loop: LOOP
--	repeat
	FETCH CursorTrabajadores INTO _id_trabajador,_id_persona,_fecha_alta;
	IF ( fin=1) THEN
-- 		Termina el loop es decir cierra el cursor
		LEAVE ciclo_loop;
	END IF; 
		 SELECT id_vac_saldo, periodo_vac, fecha_fin_periodo, fecha_ini_periodo, COUNT(*) 
			INTO _id_vac_saldo,_periodo_vac,_fecha_fin, _fecha_inicio, _contadorSaldoTrab 
			FROM p_vac_saldo 
			WHERE id_trabajador = _id_trabajador;
			-- GROUP BY id_trabajador;
			-- order by periodo_vac desc LIMIT 1;
			
		-- SELECT _id_vac_saldo,_periodo_vac,_fecha_fin, _fecha_inicio, _contadorSaldoTrab;
		
		SET _contadorCreaSaldo = 0;
		SET _insertarSaldo = 0;
		IF _contadorSaldoTrab = 0 THEN
			SET _contadorCreaSaldo = 1;
			SET _insertarSaldo = 1;
		ELSE
			SELECT  MAX(id_vac_saldo), MAX(periodo_vac), MAX(fecha_fin_periodo), MAX(fecha_ini_periodo), COUNT(*)
			INTO _id_vac_saldo,_periodo_vac,_fecha_fin, _fecha_inicio, _contadorSaldoTrab 
			FROM p_vac_saldo 
			WHERE id_trabajador = _id_trabajador GROUP BY id_trabajador;
			
			IF _fecha_fin < CURDATE() THEN    
				SET _contadorCreaSaldo = 1;
				SET _insertarSaldo = 0;
			END IF;
		END IF;	
		
		IF _contadorCreaSaldo = 1 THEN  -- no tiene record vacacional    OR _fecha_fin < CURDATE()
			IF _contadorSaldoTrab = 0  THEN
				
				SET _fecha_inicio = _fecha_alta;
				SET _fecha_fin = ADDDATE(_fecha_alta, INTERVAL 1 YEAR);
				SET _fecha_fin = ADDDATE(_fecha_fin, INTERVAL -1 DAY);
				SET _periodo_vac = YEAR(_fecha_fin);
			END IF;
			
			my_loop: LOOP
				
				IF _fecha_fin > CURDATE() THEN   -- calcula truncos perido actual
					
					SET _d_ini = DAY(_fecha_inicio);
					SET _d_hoy = DAY(CURDATE());
					SET _dia = _d_hoy - _d_ini;
					SET _m_ini = MONTH(_fecha_inicio);
					SET _m_hoy = MONTH(CURDATE());
					SET _mes = _m_hoy - _m_ini;
					SET _a_ini = YEAR(_fecha_inicio);
					SET _a_hoy = YEAR(CURDATE());
					SET _ano = _a_hoy - _a_ini;
					SET _diasTrabajados = DATEDIFF(CURDATE(),_fecha_inicio) + 1;
					
					-- 1- domingo - 2-lunes - 3-martes - 4-miercoles - 5-jueves - 6-viernes - 7-sabado
					-- DAYOFWEEK('1998-02-03');
					SET _fecha_evalua = ADDDATE(_fecha_inicio, INTERVAL -1 DAY);
					SET _ndia_fs = 0;
					loop_dia: LOOP
						SET _fecha_evalua = ADDDATE(_fecha_evalua, INTERVAL 1 DAY);
						SET _dia_evalua = DAYOFWEEK(_fecha_evalua);
						IF _dia_evalua = 7 OR _dia_evalua = 1 THEN
							SET _ndia_fs = _ndia_fs + 1;
						END IF;
						IF _fecha_evalua = CURDATE() THEN
							LEAVE loop_dia;
						END IF;
					END LOOP loop_dia;
					
					SET _diasTrabajados = _diasTrabajados - _ndia_fs;
					-- SET _ndia_fs = 0;
					
					IF _a_ini = _a_hoy THEN
						IF _mes > 0 AND _d_ini = _d_hoy THEN
							SET _truncos = _mes * 2.5;
						END IF;
						IF _mes > 0 AND _d_ini > _d_hoy THEN
							SET _dia = _d_ini - _d_hoy;
							SET _truncos = ((_mes - 1) * 2.5) + (_dia * (2.5 / 30)) ;
						END IF;
						IF _mes > 0 AND _d_ini < _d_hoy THEN
							SET _dia = _d_hoy - _d_ini;
							SET _truncos = (_mes * 2.5) + (_dia * (2.5 / 30)) ;
						END IF;
						IF _mes = 0 THEN
							SET _dia = _d_hoy - _d_ini;
							SET _truncos = (_dia * (2.5 / 30)) ;
						END IF;
					END IF;
					IF _a_hoy > _a_ini THEN
						SET _mes = (12 - _m_ini) + _m_hoy;
						
						IF _d_ini = _d_hoy THEN
							SET _truncos = _mes * 2.5;
						END IF;
						IF _d_ini > _d_hoy THEN
							SET _dia = _d_ini - _d_hoy;
							SET _truncos = ((_mes - 1) * 2.5) + (_dia * (2.5 / 30)) ;
						END IF;
						IF _d_ini < _d_hoy THEN
							SET _dia = _d_hoy - _d_ini;
							SET _truncos = (_mes * 2.5) + (_dia * (2.5 / 30)) ;
						END IF;
					END IF;
					
					INSERT INTO p_vac_saldo(
						id_trabajador,
						periodo_vac,
						fecha_ini_periodo,
						fecha_fin_periodo,
						dias_trabajados,
						dias_truncos,
						dias_ganados,
						dias_gozados,
						dias_compensados,
						dias_indemnizados,
						saldo_vac,
						comentario,
						ultimo_proceso,
						usuario
					)VALUES(
						_id_trabajador,
						_periodo_vac,
						_fecha_inicio,
						_fecha_fin,
						_diasTrabajados, 
						_truncos, 
						0,
						0,
						0,
						0,
						0,
						"Calculando Record Vacacional - Truncos",
						NOW(),
						_usuario
					);
					
					LEAVE my_loop;
				
				ELSE  -- crea sus peridos vacacionales desde el inicio de su alta o los que le falta
				
					SET _diasTrabajados = DATEDIFF(_fecha_fin,_fecha_inicio) + 1;
					
					-- 1- domingo - 2-lunes - 3-martes - 4-miercoles - 5-jueves - 6-viernes - 7-sabado
					-- DAYOFWEEK('1998-02-03');
					SET _fecha_evalua = ADDDATE(_fecha_inicio, INTERVAL -1 DAY);
					SET _ndia_fs = 0;
					loop_diaa: LOOP
						SET _fecha_evalua = ADDDATE(_fecha_evalua, INTERVAL 1 DAY);
						SET _dia_evalua = DAYOFWEEK(_fecha_evalua);
						IF _dia_evalua = 7 OR _dia_evalua = 1 THEN
							SET _ndia_fs = _ndia_fs + 1;
						END IF;
						IF _fecha_evalua = _fecha_fin THEN
							LEAVE loop_diaa;
						END IF;
					END LOOP loop_diaa;
					
					SET _diasTrabajados = _diasTrabajados - _ndia_fs;
					-- SET _ndia_fs = 0;
					IF _insertarSaldo = 1 THEN
						
						INSERT INTO p_vac_saldo(
							id_trabajador,
							periodo_vac,
							fecha_ini_periodo,
							fecha_fin_periodo,
							dias_trabajados,
							dias_truncos,
							dias_ganados,
							dias_gozados,
							dias_compensados,
							dias_indemnizados,
							saldo_vac,
							comentario,
							ultimo_proceso,
							usuario
						)VALUES(
							_id_trabajador,
							_periodo_vac,
							_fecha_inicio,
							_fecha_fin,
							_diasTrabajados, 
							0.0, 
							30,
							0,
							0,
							0,
							30,
							"Calculando Record Vacacional", -- 
							NOW(),
							_usuario
						);
					ELSE
						SET _insertarSaldo = 1;
						UPDATE p_vac_saldo SET
						dias_truncos = 0.0,
						dias_trabajados = _diasTrabajados,
						dias_ganados = 30,
						saldo_vac = 30,
						ultimo_proceso = NOW()
						WHERE id_vac_saldo = _id_vac_saldo;
						
					END IF;
					
					SET _fecha_inicio = ADDDATE(_fecha_fin, INTERVAL 1 DAY);
					SET _fecha_fin = ADDDATE(_fecha_inicio, INTERVAL 1 YEAR);
					SET _fecha_fin = ADDDATE(_fecha_fin, INTERVAL -1 DAY);
					SET _periodo_vac = YEAR(_fecha_fin);
				END IF;
			END LOOP my_loop;
			
		ELSE	  -- tiene record vacacional
			
			SELECT  MAX(id_vac_saldo), MAX(periodo_vac), MAX(fecha_fin_periodo), MAX(fecha_ini_periodo), COUNT(*)
			INTO _id_vac_saldo,_periodo_vac,_fecha_fin, _fecha_inicio, _contadorSaldoTrab 
			FROM p_vac_saldo 
			WHERE id_trabajador = _id_trabajador GROUP BY id_trabajador;
			
			IF _fecha_fin = CURDATE() THEN  -- cumple su periodo record vacacional
				
				SET _diasTrabajados = DATEDIFF(_fecha_fin,_fecha_inicio) + 1;
					
				-- 1- domingo - 2-lunes - 3-martes - 4-miercoles - 5-jueves - 6-viernes - 7-sabado
				-- DAYOFWEEK('1998-02-03');
				SET _fecha_evalua = ADDDATE(_fecha_inicio, INTERVAL -1 DAY);
				SET _ndia_fs = 0;
				loop_diaa: LOOP
					SET _fecha_evalua = ADDDATE(_fecha_evalua, INTERVAL 1 DAY);
