					END IF;
					IF _fecha_evalua = _fecha_fin THEN
						LEAVE loop_diaa;
					END IF;
				END LOOP loop_diaa;
				
				SET _diasTrabajados = _diasTrabajados - _ndia_fs;
				-- SET _ndia_fs = 0;
					
				UPDATE p_vac_saldo SET
					dias_truncos = 0,
					dias_trabajados = _diasTrabajados,
					dias_ganados = 30,
					saldo_vac = 30 - saldo_vac,
					ultimo_proceso = NOW()
					WHERE id_vac_saldo = _id_vac_saldo;
					
				INSERT INTO p_vac_saldo(
					id_trabajador,
					periodo_vac,
					fecha_ini_periodo,
					fecha_fin_periodo,
					dias_trabajados,
					dias_truncos,
					dias_ganados,
					dias_gozados,
					dias_compensados,
					dias_indemnizados,
					saldo_vac,
					comentario,
					ultimo_proceso,
					usuario
				)VALUES(
					_id_trabajador,
					_periodo_vac + 1,
					ADDDATE(_fecha_fin, INTERVAL 1 DAY),
					ADDDATE(_fecha_fin, INTERVAL 1 YEAR),
					0,
					0.1,
					0,
					0,
					0,
					0,
					0,
					"Calculando Record Vacacional",
					NOW(),
					_usuario
				);
				
			ELSE    -- calcula dias truncos record vacacional
			
				IF _fecha_fin > CURDATE() THEN
					SET _diasTrabajados = DATEDIFF(CURDATE(),_fecha_inicio) + 1;
					
					-- 1- domingo - 2-lunes - 3-martes - 4-miercoles - 5-jueves - 6-viernes - 7-sabado
					-- DAYOFWEEK('1998-02-03');
					SET _fecha_evalua = ADDDATE(_fecha_inicio, INTERVAL -1 DAY);
					SET _ndia_fs = 0;
					loop_dia: LOOP
						SET _fecha_evalua = ADDDATE(_fecha_evalua, INTERVAL 1 DAY);
						SET _dia_evalua = DAYOFWEEK(_fecha_evalua);
						IF _dia_evalua = 7 OR _dia_evalua = 1 THEN
							SET _ndia_fs = _ndia_fs + 1;
						END IF;
						IF _fecha_evalua = CURDATE() THEN
							LEAVE loop_dia;
						END IF;
					END LOOP loop_dia;
					
					SET _diasTrabajados = _diasTrabajados - _ndia_fs;
					-- SET _ndia_fs = 0;
					
					SET _d_ini = DAY(_fecha_inicio);
					SET _d_hoy = DAY(CURDATE());
					SET _dia = _d_hoy - _d_ini;
					SET _m_ini = MONTH(_fecha_inicio);
					SET _m_hoy = MONTH(CURDATE());
					SET _mes = _m_hoy - _m_ini;
					SET _a_ini = YEAR(_fecha_inicio);
					SET _a_hoy = YEAR(CURDATE());
					SET _ano = _a_hoy - _a_ini;
					
					IF _a_ini = _a_hoy THEN
						IF _mes > 0 AND _d_ini = _d_hoy THEN
							SET _truncos = _mes * 2.5;
						END IF;
						IF _mes > 0 AND _d_ini > _d_hoy THEN
							SET _dia = _d_ini - _d_hoy;
							SET _truncos = ((_mes - 1) * 2.5) + (_dia * (2.5 / 30)) ;
						END IF;
						IF _mes > 0 AND _d_ini < _d_hoy THEN
							SET _dia = _d_hoy - _d_ini;
							SET _truncos = (_mes * 2.5) + (_dia * (2.5 / 30)) ;
						END IF;
						IF _mes = 0 THEN
							SET _dia = _d_hoy - _d_ini;
							SET _truncos = (_dia * (2.5 / 30)) ;
						END IF;
					END IF;
					IF _a_hoy > _a_ini THEN
						SET _mes = (12 - _m_ini) + _m_hoy;
						
						IF _d_ini = _d_hoy THEN
							SET _truncos = _mes * 2.5;
						END IF;
						IF _d_ini > _d_hoy THEN
							SET _dia = _d_ini - _d_hoy;
							SET _truncos = ((_mes - 1) * 2.5) + (_dia * (2.5 / 30)) ;
						END IF;
						IF _d_ini < _d_hoy THEN
							SET _dia = _d_hoy - _d_ini;
							SET _truncos = (_mes * 2.5) + (_dia * (2.5 / 30)) ;
						END IF;
					END IF;
					
					UPDATE p_vac_saldo SET
						dias_truncos = _truncos,
						dias_trabajados = _diasTrabajados,
						comentario = "Calculando Record Vacacional - Dìas Truncos",
						ultimo_proceso = NOW(),
						usuario = _usuario
						WHERE id_trabajador = _id_trabajador AND periodo_vac = _periodo_vac;
						-- id_vac_saldo = _id_vac_saldo;
						
				END IF;
			END IF;
		END IF;
--	UNTIL salida END REPEAT;
	END LOOP ciclo_loop ;
	CLOSE CursorTrabajadores;
	-- select current_streak into longeststreak;
	-- select current_win into `status`;
    END */$$
DELIMITER ;

/* Procedure structure for procedure `sp_menuConfigurarMenuConsultas` */

/*!50003 DROP PROCEDURE IF EXISTS  `sp_menuConfigurarMenuConsultas` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_menuConfigurarMenuConsultas`(
	_flag CHAR(1),
	_criterio VARCHAR(50)
    )
BEGIN
	IF _flag = '1' THEN -- los dominios
		SELECT id_dominio,dominio,icon,orden,activo FROM `men_dominio` ORDER BY orden;
	END IF;
	IF _flag = '2' THEN -- dominio a editar
		SELECT id_dominio,dominio,icon,orden,activo FROM `men_dominio` WHERE id_dominio = _criterio;
	END IF;
	IF _flag = '3' THEN -- modulos de un dominio
		SELECT id_modulo,modulo,orden,activo FROM `men_modulo` WHERE `id_dominio` = _criterio ORDER BY orden;
	END IF;
	IF _flag = '4' THEN -- modulo a editar
		SELECT id_modulo,modulo,orden,activo FROM `men_modulo` WHERE `id_modulo` = _criterio;
	END IF;
	IF _flag = '5' THEN -- menu principal de un modulo
		SELECT `id_menuprincipal`,`menuprincipal`,`orden`,`activo`,alias,url FROM `men_menuprincipal` WHERE `id_modulo` = _criterio ORDER BY orden;
	END IF;
	IF _flag = '6' THEN -- menu principal a editar
		SELECT `id_menuprincipal`,`menuprincipal`,`orden`,`activo`,alias,url FROM `men_menuprincipal` WHERE `id_menuprincipal` = _criterio;
	END IF;
    END */$$
DELIMITER ;

/* Procedure structure for procedure `sp_menuConfigurarMenuDominioMantenimiento` */

/*!50003 DROP PROCEDURE IF EXISTS  `sp_menuConfigurarMenuDominioMantenimiento` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_menuConfigurarMenuDominioMantenimiento`(
	_flag CHAR(1),
	_key CHAR(2),
	_dominio VARCHAR(100),
	_icono VARCHAR(100),
	_orden INT,
	_activo CHAR(1),	
	_usuario VARCHAR(20)
    )
BEGIN
	DECLARE _duplicado INT DEFAULT 0;
	DECLARE _result INT DEFAULT 1;
	DECLARE _idDominio CHAR(2) DEFAULT '';
	DECLARE _c INT;
	
	IF _flag = '1' THEN -- nuevo
		-- verificar duplicado
		SELECT COUNT(*) INTO _duplicado FROM `men_dominio` WHERE `dominio` = _dominio;
		
		IF _duplicado = 0 THEN
			INSERT INTO `men_dominio`(
				`id_dominio`,
				`dominio`,
				`icon`,
				`activo`,
				`fechasistema`,
				`usuario`,
				orden
			)
			SELECT
				LPAD(MAX(`id_dominio`) + 1,2,0),
				_dominio,
				_icono,
				_activo,
				NOW(),
				_usuario,
				_orden
			FROM men_dominio;
		ELSE
			SET _result = 2;
		END IF;
		SELECT _result AS result,_duplicado AS duplicado;
	END IF;
	IF _flag = '2' THEN -- editar
		-- verificar duplicado
		SELECT COUNT(*) INTO _duplicado FROM `men_dominio` WHERE `dominio` = _dominio AND `id_dominio` <> _key;
		
		IF _duplicado = 0 THEN
			UPDATE `men_dominio` SET
				`dominio` = _dominio,
				`icon` = _icono,
