DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_menuConfigurarMenuMenuPrincipalMantenimiento`(
	_flag CHAR(1),
	_key CHAR(4),
	_idModulo CHAR(3),
	_menu VARCHAR(100),
	_orden INT,
	_url varchar(300),
	_alias char(5),
	_activo CHAR(1),
	_usuario VARCHAR(20)
    )
BEGIN
	DECLARE _duplicaOpcion INT DEFAULT 0;
	DECLARE _duplicaAlias INT DEFAULT 0;
	DECLARE _duplicaUrl INT DEFAULT 0;
	-- DECLARE _duplicado INT DEFAULT 0;
	DECLARE _result INT DEFAULT 1;
	DECLARE _c INT;
	DECLARE _idMenu CHAR(4) DEFAULT '';
	
	SET @duplicado = 0;
	
	IF _flag = '1' THEN -- nuevo
		-- verificar duplicado
		SELECT COUNT(*) INTO _duplicaOpcion FROM `men_menuprincipal` WHERE `menuprincipal` = _menu AND `id_modulo` = _idModulo;
		SELECT COUNT(*) INTO _duplicaAlias FROM `men_menuprincipal` WHERE `alias` = _alias;
		SELECT COUNT(*) INTO _duplicaUrl FROM `men_menuprincipal` WHERE `url` = _url;
		
		IF _duplicaOpcion = 1 THEN
			SET @duplicado = 1;
			SET _result = 2;
		ELSEIF _duplicaAlias = 1 THEN
			SET @duplicado = 1;
			SET _result = 3;
		ELSEIF _duplicaUrl = 1 THEN
			SET @duplicado = 1;
			SET _result = 4;
		ELSE
			INSERT INTO `men_menuprincipal`(
				`id_menuprincipal`,
				`id_modulo`,
				`menuprincipal`,
				`orden`,
				`activo`,
				`fechasistema`,
				`usuario`,
				`alias`,
				`url`				
			)
			SELECT
				LPAD(MAX(`id_menuprincipal`) + 1,4,0),
				_idModulo,
				_menu,
				_orden,
				_activo,
				NOW(),
				_usuario,
				_alias,
				_url
			FROM men_menuprincipal;
		END IF;
		SELECT _result AS result,@duplicado AS duplicado;
	END IF;
	IF _flag = '2' THEN -- editar
		-- verificar duplicado
		SELECT COUNT(*) INTO _duplicaOpcion FROM `men_menuprincipal` WHERE `menuprincipal` = _menu AND `id_modulo` = _idModulo AND `id_menuprincipal` <> _key;
		SELECT COUNT(*) INTO _duplicaAlias FROM `men_menuprincipal` WHERE `alias` = _alias AND id_menuprincipal <> _key;
		SELECT COUNT(*) INTO _duplicaUrl FROM `men_menuprincipal` WHERE `url` = _url AND id_menuprincipal <> _key;
		
		IF _duplicaOpcion = 1 THEN
			SET @duplicado = 1;
			SET _result = 2;
		ELSEIF _duplicaAlias = 1 THEN
			SET @duplicado = 1;
			SET _result = 3;
		ELSEIF _duplicaUrl = 1 THEN
			SET @duplicado = 1;
			SET _result = 4;
		ELSE
			UPDATE `men_menuprincipal` SET
				`menuprincipal` = _menu,
				`orden` = _orden,
				`activo` = _activo,
				`alias` = _alias,
				`url` = _url
			WHERE id_menuprincipal = _key;
		END IF;
		SELECT _result AS result,@duplicado AS duplicado;
	END IF;
	IF _flag = '3' THEN -- delete
		DELETE FROM men_menuprincipal WHERE id_menuprincipal = _key;
		SELECT _result AS result;
	END IF;
	IF _flag = '4' THEN -- ordenar menu principal
		SET _c = 1;
		WHILE _c <= _orden DO
			SET _idMenu = `fnExplode`(_menu,',',_c);
			
			UPDATE `men_menuprincipal` SET
				orden = _c
			WHERE `id_menuprincipal` = _idMenu;
			
			SET _c = _c + 1;
		END WHILE;
		SELECT _result AS result;
	END IF;
    END */$$
DELIMITER ;

