/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_perDerechohabienteGrid`(
	_idTrabajador int,
	_iDisplayStart INT,
        _iDisplayLength INT, 
        _iOrderCol VARCHAR(200),
        _sSearch VARCHAR(200),
        _sFilterCols VARCHAR(300)
    )
BEGIN
	DECLARE orderby VARCHAR(300) DEFAULT '';
	DECLARE isearch VARCHAR(300) DEFAULT '';
	DECLARE filterCols VARCHAR(300) DEFAULT '';
	
	-- IF _sSearch <> '' THEN
		-- SET isearch = CONCAT(' AND m.modulo LIKE "%',_sSearch,'%" ');
	-- END IF;
	IF _sFilterCols <> '' THEN
		SET filterCols = REPLACE(_sFilterCols,'&quot;','"');
		SET filterCols = REPLACE(filterCols,'*','%');
	END IF;
	IF _iOrderCol <> '' THEN
		SET orderby = CONCAT(' ORDER BY ',_iOrderCol);
	END IF;
	
	SET @sentencia = CONCAT('
	SELECT
		COUNT(*) INTO @countx
	FROM
	(
		SELECT 
			t.vinculofamiliar,
			CONCAT(d.apellidos," ",d.nombres) AS nombres,
			d.numerodocumento,
			d.telefonos,
			d.email,
			d.estado as estadod,
			d.id_derechohabiente
		FROM p_derechohabiente d
		INNER JOIN m_tipovinculofamiliar t ON t.id_tipovinculofamiliar = d.id_tipovinculofamiliar
		WHERE d.id_trabajador = "',_idTrabajador,'"
	)AS F
	WHERE estadod != "E"
	',isearch,' ',filterCols,' ;');
	
	PREPARE consulta FROM @sentencia;
	EXECUTE consulta;
	DEALLOCATE PREPARE consulta;
	SET @sentencia = NULL;
	
	SET @sentencia = CONCAT('
	SELECT
		total,
		id_derechohabiente,
		vinculofamiliar,
		nombres,
		numerodocumento,
		telefonos,
		email,
		estadod
	FROM
	(
		SELECT 
			@countx as total,
			t.vinculofamiliar,
			CONCAT(d.apellidos," ",d.nombres) AS nombres,
			d.numerodocumento,
			d.telefonos,
			d.email,
			d.estado as estadod,
			d.id_derechohabiente
		FROM p_derechohabiente d
		INNER JOIN m_tipovinculofamiliar t ON t.id_tipovinculofamiliar = d.id_tipovinculofamiliar
		WHERE d.id_trabajador = "',_idTrabajador,'"
	)AS F
	WHERE estadod != "E"
	',isearch,' ',filterCols,' ',orderby,' LIMIT ',_iDisplayStart,',',_iDisplayLength,'; ');
		
	PREPARE consulta FROM @sentencia;
	EXECUTE consulta;
	DEALLOCATE PREPARE consulta;
	SET @sentencia = NULL;
    END */$$
