DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_perDerechohabienteMantenimiento`(
	_flag int,
	_key int,
	_idTrabajador int,
	_idTipoVinculoFamiliar int,
	_tipoDocIdentidad int,
	_numerodocumento varchar(20),
	_nombres VARCHAR(250),
	_apellidos VARCHAR(250),
	_sexo char(1),
	_ubigeoDireccion char(6),
	_direccion VARCHAR(300),
	_email VARCHAR(150),
	_telefonos VARCHAR(150),
	_estado char(1),
	_usuario char(8)
    )
BEGIN
	DECLARE _result INT DEFAULT 1;
	DECLARE _duplicado INT DEFAULT 0;
	
	-- INSERT --
	IF _flag = '1' THEN 
		SELECT COUNT(*) INTO _duplicado FROM p_derechohabiente WHERE (
									REPLACE(CONCAT(nombres,apellidos),' ','')  = REPLACE(CONCAT(_nombres,_apellidos),' ','') 
									OR 
									numerodocumento = _numerodocumento
									)
									and id_trabajador = _idTrabajador;
		IF _duplicado = 0 THEN
			insert into p_derechohabiente(
				id_trabajador,
				id_tipovinculofamiliar,
				id_tipodocumentoidentidad,
				numerodocumento,
				nombres,
				apellidos,
				sexo,
				id_ubigeodireccion,
				direccion,
				email,
				telefonos,
				usuario_creacion
			)values(
				_idTrabajador,
				_idTipoVinculoFamiliar,
				_tipoDocIdentidad,
				_numerodocumento,
				_nombres,
				_apellidos,
				_sexo,
				_ubigeoDireccion,
				_direccion,
				_email,
				_telefonos,
				_usuario
			);
		else -- ya existe
			SET _result = 2; 
		end if;
		SELECT _result AS result,_duplicado AS duplicado;
	end if;
	-- UPDATE --
	IF _flag = '2' THEN
		SELECT COUNT(*) INTO _duplicado FROM p_derechohabiente WHERE (
									REPLACE(CONCAT(nombres,apellidos),' ','')  = REPLACE(CONCAT(_nombres,_apellidos),' ','') 
									OR 
									numerodocumento = _numerodocumento
									)
									AND id_trabajador = _idTrabajador
									and id_derechohabiente != _key;
		IF _duplicado = 0 THEN
			update p_derechohabiente set
				id_tipovinculofamiliar = _idTipoVinculoFamiliar,
				id_tipodocumentoidentidad = _tipoDocIdentidad,
				numerodocumento = _numerodocumento,
				nombres = _nombres,
				apellidos = _apellidos,
				sexo = _sexo,
				id_ubigeodireccion = _ubigeoDireccion,
				direccion = _direccion,
				email = _email,
				telefonos = _telefonos,
				usuario_modifica = _usuario,
				fecha_modifica = now()
			where id_derechohabiente = _key;
		ELSE -- ya existe
			SET _result = 2; 
		END IF;
		SELECT _result AS result,_duplicado AS duplicado;
	end if;
	-- LOGIC DELETE --
	IF _flag = '3' THEN
		UPDATE p_derechohabiente SET estado = 'E' WHERE id_derechohabiente = _key;
		SELECT _result AS result;
	END IF;
    END */$$
DELIMITER ;

