/* Procedure structure for procedure `sp_perPersonalCargoMantenimiento` */

/*!50003 DROP PROCEDURE IF EXISTS  `sp_perPersonalCargoMantenimiento` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_perPersonalCargoMantenimiento`(
	_flag int,
	_key int,
	_idTtrabajador int,
	_idCargo int,
	_idArea int,
	_principal int,
	_estado char(1),
	_usuario char(8)
    )
BEGIN
	DECLARE _result INT DEFAULT 1;
	DECLARE _duplicado INT DEFAULT 0;
	
	-- INSERT --
	IF _flag = 1 THEN -- nuevo
		SELECT COUNT(*) INTO _duplicado FROM p_cargotrabajador WHERE id_trabajador = _idTtrabajador
									and id_cargo = _idCargo
									and id_area = _idArea
									AND estado != 'E';
								
		IF _duplicado = 0 THEN
			-- si es principal, update princial = 0
			IF _principal = 1 THEN
				UPDATE p_cargotrabajador SET principal = '0' WHERE id_trabajador = _idTtrabajador;
			END IF;
			
			INSERT INTO p_cargotrabajador(
				id_trabajador,
				id_cargo,
				id_area,
				principal,
				estado,
				usuario_creacion
			)VALUES(
				_idTtrabajador,
				_idCargo,
				_idArea,
				_principal,
				_estado,
				_usuario
			);
		ELSE 
			SET _result = 2; -- duplicado
		END IF;
		SELECT _result AS result,_duplicado AS duplicado;
	END IF;
	-- UPDATE
	IF _flag = 2 THEN -- nuevo
		SELECT COUNT(*) INTO _duplicado FROM p_cargotrabajador WHERE id_trabajador = _idTtrabajador
									AND id_cargo = _idCargo
									AND id_area = _idArea
									AND estado != 'E'
									and id_cargotrabajador != _key;
								
		IF _duplicado = 0 THEN
			-- si es principal, update princial = 0
			IF _principal = 1 THEN
				UPDATE p_cargotrabajador SET principal = '0' WHERE id_trabajador = _idTtrabajador;
			END IF;
			
			update p_cargotrabajador set
				principal = _principal,
				estado = _estado
			where id_cargotrabajador = _key;
		ELSE 
			SET _result = 2; -- duplicado
		END IF;
		SELECT _result AS result,_duplicado AS duplicado;
	END IF;
	-- LOGIC DELETE --
	IF _flag = 3 THEN
		UPDATE p_cargotrabajador SET estado = 'E' WHERE id_cargotrabajador = _key;
		SELECT _result AS result;
	END IF;
    END */$$
DELIMITER ;

