/* Procedure structure for procedure `sp_perPersonalConsultas` */

/*!50003 DROP PROCEDURE IF EXISTS  `sp_perPersonalConsultas` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_perPersonalConsultas`(
	_flag int,
	_criterio varchar(50)
    )
BEGIN
	-- SELECT TIPO DE DOCUMENTO
	if _flag = 1 then
		select 
			id_tipodocumentoidentidad,
			tipodocumentoidentidad 
		from m_tipodocumentoidentidad
		where estado = 'A'
		order by 2;
	end if;
	-- SELECT ACTIVIDADES
	IF _flag = 2 THEN
		SELECT 
			id_actividad,
			actividad 
		FROM m_actividad
		WHERE estado = 'A'
		ORDER BY 2;
	END IF;
	-- SELECT DEPARTAMENTOS
	IF _flag = 3 THEN
		SELECT 
			id_departamento,
			departamento 
		FROM ub_departamento
		ORDER BY 2;
	END IF;
	-- SELECT PROVINCIA DE UN DEPARTAMENTO
	IF _flag = 4 THEN
		SELECT 
			id_provincia,
			provincia 
		FROM ub_provincia
		WHERE left(id_provincia,2) = _criterio
		ORDER BY 2;
	END IF;
	-- SELECT DISTRITO DE UNA PROVINCIA 
	IF _flag = 5 THEN
		SELECT 
			id_ubigeo,
			distrito 
		FROM ub_ubigeo
		WHERE Left(id_ubigeo,4) = _criterio
		ORDER BY 2;
	END IF;
	-- SELECT EMPLEADO A TRABAJAR 
	IF _flag = 6 THEN
		SELECT
			p.apellidopaterno,
			p.apellidomaterno,
			p.primernombre,
			p.segundonombre,
			p.sexo,
			p.direccion,
			p.email,
			p.estado,
			p.id_tipodocumentoidentidad,
			p.numerodocumento,
			p.telefono,
			t.fecha_nacimiento,
			t.id_ubigeodireccion,
			t.id_ubigeonacimeinto
		FROM p_trabajador t
		INNER JOIN mae_persona p ON p.id_persona = t.id_persona
		WHERE t.id_persona = _criterio;
	end if;
	-- SELECT TIPO VINCULO FAMILIAR 
	IF _flag = 7 THEN
		SELECT 
			id_tipovinculofamiliar,
			vinculofamiliar 
		FROM m_tipovinculofamiliar
		WHERE estado = 'A'
		ORDER BY 2;
	END IF;
	-- SELECT DERECHOHABIENTE A EDITAR
	IF _flag = 8 THEN
		SELECT 
			id_tipovinculofamiliar,
			id_tipodocumentoidentidad,
			numerodocumento,
			nombres,
			apellidos,
			sexo,
			id_ubigeodireccion,
			direccion,
			email,
			telefonos 
		FROM p_derechohabiente
		WHERE id_derechohabiente = _criterio;
	END IF;
	-- SELECT HORARIO A EDITAR
	IF _flag = 9 THEN
		select 
			horario,
			hora_ingreso_m,
			hora_salida_m,
			hora_ingreso_t,
			hora_salida_t,
			estado
		from p_horario
		where id_horario = _criterio;
	end if;
	-- SELECT SISTEMA PENSION A EDITAR
	IF _flag = 10 THEN
		SELECT 
			ruc,
			sistemapension,
			estado
		FROM m_sistemapension
		WHERE id_sistemapension = _criterio;
	END IF;
	-- SELECT TIPO PLANILLA A EDITAR
	IF _flag = 11 THEN
		SELECT 
			tipoplanilla,
			estado
		FROM m_tipoplanilla
		WHERE id_tipoplanilla = _criterio;
	END IF;
	-- SELECT LISTADO TIPO DE PAGO
	IF _flag = 12 THEN
		SELECT 
			id_tipopago,
			tipopago
		FROM m_tipopago
		WHERE estado = 'A';
	END IF;
	-- SELECT LISTADO TIPO DE CONTRATO
	IF _flag = 13 THEN
		SELECT 
			id_tipocontrato,
			contrato
		FROM m_tipocontrato
		WHERE estado = 'A';
	END IF;
	-- SELECT LISTADO DE HORARIOS
	IF _flag = 14 THEN
		SELECT 
			id_horario,
			horario
		FROM p_horario
		WHERE estado = 'A';
	END IF;
	-- SELECT LISTADO DE CATEGORIAS DE TRABAJADOR
	IF _flag = 15 THEN
		SELECT 
			id_categoriatrabajador,
			categoriatrabajador
		FROM m_categoriatrabjador
		WHERE estado = 'A';
	END IF;
	-- SELECT LISTADO DE AREAS
	IF _flag = 16 THEN
		SELECT 
			a.dependencia,
			(SELECT `area` FROM m_area WHERE id_area = a.dependencia)AS subarea,
			a.id_area,
			a.area,
			a.dependencia AS dependencia2	
		FROM m_area a
		WHERE estado = 'A'
		AND id_area > 1
		ORDER BY a.area;
	END IF;
	-- SELECT LISTADO DE CATEGORIAS DE TRABAJADOR
	IF _flag = 17 THEN
		SELECT 
			id_cargo,
			cargo
		FROM m_cargo
		WHERE estado = 'A';
	END IF;
	-- SELECT CARGO TRABAJADOR A EDITAR
	IF _flag = 18 THEN
		SELECT 
			id_cargo,
			id_area,
			principal,
			estado
		FROM p_cargotrabajador
		WHERE id_cargotrabajador = _criterio;
	END IF;
	-- SELECT DATOS EDITAR
	IF _flag = 19 THEN
		SELECT 
			id_actividad,
			id_categoriatrabajador,
			id_tipocontrato,
			id_tipopago,
			id_horario,
			estabiblidad,
			id_cuentacorrienteH,
			id_cuentacorrienteCTS,
			(select id_banco from m_cuentacorriente where id_cuentacorriente = id_cuentacorrienteH)as bancoH,
			(SELECT id_banco FROM m_cuentacorriente WHERE id_cuentacorriente = id_cuentacorrienteCTS)AS bancoCTS,
			id_sistemapension,
			codigo_sistemapension,
			date_format(fecha_inscripcion_sistemapension,'%d-%m-%Y')as fecha_inscripcion_sistemapension,
			tipo_aplicacion_sistemapension,
			monto_sistemapension,
			codigo_essalud,
			date_format(fecha_alta,'%d-%m-%Y')as fecha_alta,
			DATE_FORMAT(fecha_baja,'%d-%m-%Y')AS fecha_baja,
			id_tipoplanilla,
			sueldo,
			id_motivobaja,
			sistemapension_variante as variante
		FROM p_trabajador
		WHERE id_trabajador = _criterio;
	END IF;
	-- SELECT LISTADO DE BANCOS
	IF _flag = 20 THEN
		SELECT 
			id_banco,
			banco
		FROM m_banco
		WHERE estado = 'A';
	END IF;
	-- SELECT CUENTA CORRIENTE DE BANCO
	IF _flag = 21 THEN
		SELECT 
			id_cuentacorriente,
			cuentacorriente
		FROM m_cuentacorriente
		WHERE estado = 'A'
		AND id_banco = _criterio;
	END IF;
	-- SELECT LISTA SISTEMA PENSIONES
	IF _flag = 22 THEN
		SELECT 
			id_sistemapension,
			sistemapension
		FROM m_sistemapension
		WHERE estado = 'A';
	END IF;
	-- SELECT LISTA TIPO DE PLANILLA
	IF _flag = 23 THEN
		SELECT 
			id_tipoplanilla,
			tipoplanilla
		FROM m_tipoplanilla
		WHERE estado = 'A';
	END IF;
	-- SELECT LISTA MOTIVO BAJA
	IF _flag = 24 THEN
		SELECT 
			id_motivobaja,
			motivobaja
		FROM p_motivobaja
		WHERE estado = 'A';
	END IF;
	-- SELECT MOTIVO BAJA A EDITAR
	IF _flag = 25 THEN
		SELECT 
			id_motivobaja,
			motivobaja,
			estado
		FROM p_motivobaja
		WHERE id_motivobaja = _criterio;
	END IF;
	-- SELECT LISTA CONCEPTOS
	IF _flag = 26 THEN
		SELECT 
			id_conceptoplanilla,
			conceptoplanilla
		FROM p_conceptoplanilla
		WHERE estado = 'A';
	END IF;
	-- SELECT DATOS PARA CONTRATO
	IF _flag = 27 THEN
		SELECT
			tc.id_tipocontrato,
			p.numerodocumento,
			p.nombrecompleto,
			tc.contenido,
			p.direccion,
			u.distrito,
			(
				SELECT cc.cargo FROM p_cargotrabajador c 
				INNER JOIN m_cargo cc ON cc.id_cargo = c.id_cargo
				WHERE c.id_trabajador = t.id_trabajador AND c.principal = '1'
			)AS cargo,
			(
				SELECT a.area FROM p_cargotrabajador c 
				INNER JOIN m_area a ON a.id_area = c.id_area
				WHERE c.id_trabajador = t.id_trabajador AND c.principal = '1'
			)AS aarea,
			(SELECT DATE_FORMAT(fecha_inicio,'%d-%m-%Y') FROM p_contrato ct WHERE ct.id_trabajador = 4 AND ct.estado = 'A')AS fe_ini,
			(SELECT DATE_FORMAT(fecha_fin,'%d-%m-%Y') FROM p_contrato ct WHERE ct.id_trabajador = 4 AND ct.estado = 'A')AS fe_fin
			
		FROM p_trabajador t
		INNER JOIN mae_persona p ON p.id_persona = t.id_persona
		INNER JOIN m_tipocontrato tc ON tc.id_tipocontrato = t.id_tipocontrato
		INNER JOIN ub_ubigeo u ON u.id_ubigeo = t.id_ubigeodireccion
		WHERE t.id_trabajador = _criterio;
	END IF;
    END */$$
DELIMITER ;

