			(SELECT DATE_FORMAT(fecha_fin,"%d-%m-%Y") FROM p_contrato ct WHERE ct.id_trabajador=t.id_trabajador AND ct.estado = "A" LIMIT 1)AS fe_fin
		FROM p_trabajador t
		INNER JOIN mae_persona p ON p.id_persona = t.id_persona
