DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_perPersonalDatosMantenimiento`(
	_flag int,
	_idTrabajador int,
	_idActividad int,
	_idTipoPago int,
	_idTipoContrato int,
	_idHorario int,
	_idCategoria int,
	_estabilidad char(1),
	_idCuentacorrienteH INT,
	_idCuentacorrienteCTS INT,
	_idSispension int,
        _codigoSispension varchar(30),
        _fechaInscripcion date,
        _tipoDescuento char(1),
        _montosuspension double(15,5),
        _codigoEssalud varchar(20),
        _fechaAlta date,
        _tipoPlanilla int,
        _sueldo double(15,5),
        _motivoBaja int,
        _fechaBaja date,
        _variante char(1)
	)
BEGIN
	declare _result int default 1;
	
	if _flag = 1 then -- GRABAR DATOS
		update p_trabajador set
			id_actividad = _idActividad,
			id_tipopago = _idTipoPago,
			id_tipocontrato = _idTipoContrato,
			id_horario = _idHorario,
			id_categoriatrabajador = _idCategoria,
			estabiblidad = _estabilidad,
			codigo_essalud = _codigoEssalud
		where id_trabajador = _idTrabajador;
	end if;
	IF _flag = 2 THEN -- GRABAR DATOS BANCARIOS
		UPDATE p_trabajador SET
			id_cuentacorrienteH = _idCuentacorrienteH,
			id_cuentacorrienteCTS = _idCuentacorrienteCTS
		WHERE id_trabajador = _idTrabajador;
	END IF;
	IF _flag = 3 THEN -- GRABAR SISTEMA PENSION
		UPDATE p_trabajador SET
			id_sistemapension = _idSispension,
			codigo_sistemapension = _codigoSispension,
			fecha_inscripcion_sistemapension = _fechaInscripcion,
			tipo_aplicacion_sistemapension = _tipoDescuento,
			monto_sistemapension = _montosuspension,
			sistemapension_variante = _variante
		WHERE id_trabajador = _idTrabajador;
	END IF;
	IF _flag = 4 THEN -- GRABAR ALTA
		UPDATE p_trabajador SET
			fecha_alta = _fechaAlta,
			id_tipoplanilla = _tipoPlanilla,
			sueldo = _sueldo
		WHERE id_trabajador = _idTrabajador;
	END IF;
	IF _flag = 5 THEN -- GRABAR BAJA
		UPDATE p_trabajador SET
			fecha_baja = _fechaBaja,
			id_motivobaja = _motivoBaja
		WHERE id_trabajador = _idTrabajador;
	END IF;
	select _result as result,1 as duplicado;
    END */$$
DELIMITER ;

/* Procedure structure for procedure `sp_perPersonalGenerarContratoMantenimiento` */

/*!50003 DROP PROCEDURE IF EXISTS  `sp_perPersonalGenerarContratoMantenimiento` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_perPersonalGenerarContratoMantenimiento`(
	_flag CHAR(1),
	_key INT,
	_cadena text,
	_length int,
	_usuario CHAR(8)
    )
BEGIN
	DECLARE _result INT DEFAULT 1;
	declare _c int;
	declare _idTrabajador int;
	declare _feIni date;
	declare _fefin date;
	declare _bloque text;
	
	-- INSERT --
	IF _flag = '1' THEN -- nuevo	
		SET _c = 1;
		WHILE (_c <= _length) DO
			set _bloque = fnExplode(_cadena,'#',_c);		
			set _idTrabajador = fnExplode(_bloque,'*',1);
			SET _feIni = fnExplode(_bloque,'*',2);
			SET _fefin = fnExplode(_bloque,'*',3);
			
			INSERT INTO p_contrato(
				id_trabajador,
				fecha_inicio,
				fecha_fin,
				usuario_creacion
			)VALUES(
				_idTrabajador,
				_feIni,
				_fefin,
				_usuario
			);
			
			SET _c=_c + 1;
		END WHILE;
		SELECT _result AS result;
	END IF;
	-- LOGIC DELETE --
	IF _flag = '3' THEN
		UPDATE p_contrato SET estado = 'E' WHERE id_contrato = _key;
		SELECT _result AS result;
	END IF;
    END */$$
DELIMITER ;

