DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_perPersonalMantenimiento`(
	_flag int,
	_key int,
	_primerNombre varchar(100),
        _segundoNombre VARCHAR(100),
        _apellidoPaterno VARCHAR(100),
        _apellidoMaterno VARCHAR(100),
        _sexo char(1),
        _email VARCHAR(100),
        _telefonos VARCHAR(100),
        _tipoDocumento bigint(20),
        _numeroDocumento VARCHAR(20),
        _ubigeoNacimiento char(6),
        _fechaNacimiento date,
        _ubigeoDireccion char(6),
        _direccion VARCHAR(500),
        _estado char(1),
        _usuario char(8)
    )
BEGIN
	DECLARE _result INT DEFAULT 1;
	DECLARE _duplicado INT DEFAULT 0;
	declare _idPersona int;
	
	-- INSERT --
	IF _flag = '1' THEN -- nuevo
		SELECT COUNT(*) INTO _duplicado FROM mae_persona WHERE (
									REPLACE(CONCAT(primernombre,segundonombre,apellidomaterno,apellidopaterno),' ','')  = REPLACE(concat(_primerNombre,_segundoNombre,_apellidoPaterno,_apellidoMaterno),' ','') 
									OR 
									numerodocumento = _numeroDocumento
									);
		-- si email es diferente de vacio se valida
		if _email != '' and _duplicado = 0 then
			SELECT COUNT(*) INTO _duplicado FROM mae_persona WHERE replace(email,' ','') = REPLACE(_email,' ','');
		end if;
								
		IF _duplicado = 0 THEN
			INSERT INTO mae_persona(
				persona,
				apellidopaterno,
				apellidomaterno,
				primernombre,
				segundonombre,
				nombrecompleto,
				sexo,
				direccion,
				email,
				telefono,
				id_tipodocumentoidentidad,
				numerodocumento,
				usuario_creacion
			)
			select
				LPAD(MAX(persona) + 1,8,0),
				_apellidoPaterno,
				_apellidoMaterno,
				_primerNombre,
				_segundoNombre,
				concat(_apellidoPaterno,' ',_apellidoMaterno,' ',_primerNombre,' ',_segundoNombre),
				_sexo,
				_direccion,
				_email,
				_telefonos,
				_tipoDocumento,
				_numeroDocumento,
				_usuario
			from mae_persona;
			
			select last_insert_id() into _idPersona;
			
			-- algunos datos de trabajador
			insert into p_trabajador(
				id_persona,
				id_ubigeonacimeinto,
				id_ubigeodireccion,
				fecha_nacimiento,
				usuario_creacion
			)values(
				_idPersona,
				_ubigeoNacimiento,
				_ubigeoDireccion,
				_fechaNacimiento,
				_usuario
			);
		ELSE 
			SET _result = 2; -- duplicado
		END IF;
		SELECT _result AS result,_duplicado AS duplicado;
	END IF;
	-- UPDATE --
	IF _flag = '2' THEN
		SELECT COUNT(*) INTO _duplicado FROM mae_persona WHERE (
									REPLACE(CONCAT(primernombre,segundonombre,apellidomaterno,apellidopaterno),' ','')  = REPLACE(CONCAT(_primerNombre,_segundoNombre,_apellidoPaterno,_apellidoMaterno),' ','') 
									OR 
									numerodocumento = _numeroDocumento
									)
									AND id_persona <> _key;
		-- si email es diferente de vacio se valida
		IF _email != '' AND _duplicado = 0 THEN
			SELECT COUNT(*) INTO _duplicado FROM mae_persona WHERE REPLACE(email,' ','') = REPLACE(_email,' ','')
									AND id_persona <> _key;
		end IF;
		
		IF _duplicado = 0 THEN
			UPDATE mae_persona SET
				apellidopaterno = _apellidoPaterno,
				apellidomaterno = _apellidoMaterno,
				primernombre = _primerNombre,
				segundonombre = _segundoNombre,
				nombrecompleto = CONCAT(_apellidoPaterno,' ',_apellidoMaterno,' ',_primerNombre,' ',_segundoNombre),
				sexo = _sexo,
				direccion = _direccion,
				email = _email,
				telefono = _telefonos,
				id_tipodocumentoidentidad = _tipoDocumento,
				numerodocumento = _numeroDocumento,
				usuario_modifica = _usuario,
				fecha_modifica = NOW()
			WHERE id_persona = _key;
			
			-- update trabajador
			update p_trabajador set
				id_ubigeonacimeinto = _ubigeoNacimiento,
				id_ubigeodireccion = _ubigeoDireccion,
				fecha_nacimiento = _fechaNacimiento,
				usuario_modifica = _usuario,
				fecha_modifica = now()
			where id_persona = _key;
		ELSE -- duplicado
			SET _result = 2; 
		END IF;
		SELECT _result AS result,_duplicado AS duplicado;
	END IF;
	-- LOGIC DELETE --
	IF _flag = '3' THEN
		UPDATE mae_persona SET estado = 'E' WHERE id_persona = _key;
		UPDATE p_trabajador SET estado = 'E' WHERE id_persona = _key;
		SELECT _result AS result;
	END IF;
    END */$$
DELIMITER ;

