	END IF;
	IF _iOrderCol <> '' THEN
		SET orderby = CONCAT(' ORDER BY ',_iOrderCol);
	END IF;
	
	SET @sentencia = CONCAT('
	SELECT 	
		COUNT(*) INTO @countx
	FROM m_tipoplanilla a
	WHERE estado != "E"
	',isearch,' ',filterCols,' ;');
	
	PREPARE consulta FROM @sentencia;
	EXECUTE consulta;
	DEALLOCATE PREPARE consulta;
	SET @sentencia = NULL;
	
	SET @sentencia = CONCAT('
	SELECT 	
		@countx as total,
		a.id_tipoplanilla,
		a.tipoplanilla,
		a.estado
	FROM m_tipoplanilla a
	WHERE estado != "E"
	',isearch,' ',filterCols,' ',orderby,' LIMIT ',_iDisplayStart,',',_iDisplayLength,'; ');
		
	PREPARE consulta FROM @sentencia;
	EXECUTE consulta;
	DEALLOCATE PREPARE consulta;
	SET @sentencia = NULL;
    END */$$
