/* Procedure structure for procedure `sp_perTipoPlanillaMantenimiento` */

/*!50003 DROP PROCEDURE IF EXISTS  `sp_perTipoPlanillaMantenimiento` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_perTipoPlanillaMantenimiento`(
	_flag CHAR(1),
	_key INT,
	_tipoplanilla VARCHAR(300),
	_estado CHAR(1),
	_usuario CHAR(8)
    )
BEGIN
	DECLARE _result INT DEFAULT 1;
	DECLARE _duplicado INT DEFAULT 0;
	
	-- INSERT --
	IF _flag = '1' THEN -- nuevo
		SELECT COUNT(*) INTO _duplicado FROM m_tipoplanilla WHERE REPLACE(tipoplanilla,' ','') = REPLACE(_tipoplanilla,' ','')
									AND estado != 'E';
								
		IF _duplicado = 0 THEN
			INSERT INTO m_tipoplanilla(
				tipoplanilla,
				estado,
				usuario_creacion
			)VALUES(
				_tipoplanilla,
				_estado,
				_usuario
			);
		ELSE 
			SET _result = 2; -- duplicado
		END IF;
		SELECT _result AS result,_duplicado AS duplicado;
	END IF;
	-- UPDATE --
	IF _flag = '2' THEN
		SELECT COUNT(*) INTO _duplicado FROM m_tipoplanilla WHERE REPLACE(tipoplanilla,' ','') = REPLACE(_tipoplanilla,' ','')
									AND estado != 'E'
									AND id_tipoplanilla <> _key;
		
		IF _duplicado = 0 THEN
			UPDATE m_tipoplanilla SET
				tipoplanilla = _tipoplanilla,
				estado = _estado
			WHERE id_tipoplanilla = _key;
		ELSE -- duplicado
			SET _result = 2; 
		END IF;
		SELECT _result AS result,_duplicado AS duplicado;
	END IF;
	-- LOGIC DELETE --
	IF _flag = '3' THEN
		UPDATE m_tipoplanilla SET estado = 'E' WHERE id_tipoplanilla = _key;
		SELECT _result AS result;
	END IF;
    END */$$
DELIMITER ;

/* Procedure structure for procedure `sp_pnllaRetencionJudicialConsultas` */

/*!50003 DROP PROCEDURE IF EXISTS  `sp_pnllaRetencionJudicialConsultas` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_pnllaRetencionJudicialConsultas`(
	_flag CHAR(1),
	_criterio VARCHAR(50)
)
BEGIN
	#1: trabajadores activos
	#2: beneficiarios según trabajador
	#3: concepto por el cual se va hacer la retención
	#4: bancos activos
	#5: cuentas corrientes por banco
	
	IF _flag = 1 THEN
		SELECT t.id_trabajador,CONCAT(p.nombrecompleto,' - ',p.numerodocumento) AS nomTrabajador
		FROM p_trabajador t
		INNER JOIN mae_persona p ON p.id_persona=t.id_persona
		WHERE t.estado='A';
	
	ELSEIF _flag = 2 THEN
		SELECT d.id_derechohabiente,
			CONCAT(d.apellidos,' ',d.nombres, ' - ',d.numerodocumento) AS nombres
		FROM p_derechohabiente d 
		WHERE d.id_trabajador=_criterio;
		
	ELSEIF _flag = 3 THEN
		SELECT id_conceptoplanilla,conceptoplanilla
		FROM p_conceptoplanilla 
		WHERE estado='A'
		ORDER BY 2;	
		
	ELSEIF _flag = 4 THEN
		SELECT id_banco,banco FROM m_banco WHERE estado='A';
		
	ELSEIF _flag = 5 THEN		
		SELECT id_cuentacorriente,cuentacorriente 
		FROM m_cuentacorriente 
		WHERE id_banco=_criterio;
		
	ELSEIF _flag = 6 THEN				
		SELECT r.id_retencionjudicial,
			t.id_trabajador,
			CONCAT(p.nombrecompleto,' - ',p.numerodocumento) AS nomTrabajador,
			r.id_derechohabiente,
			CONCAT(d.nombres,' - ',d.numerodocumento) AS nomBeneficiario,	
			DATE_FORMAT(r.fecha_inicio,'%d-%m-%Y') AS fecha_inicio, 
			DATE_FORMAT(r.fecha_fin,'%d-%m-%Y') AS fecha_fin,
			r.id_conceptoplanilla,
			c.conceptoplanilla,
			r.tipo_aplicacion,
			r.id_cuentacorriente,
			r.monto,
			ba.id_banco,
			ba.banco AS nombanco,
			cta.id_cuentacorriente,
			cta.cuentacorriente AS nomCuenta,
			r.observacion,
			r.estado
		FROM p_retencionjudicial r 
		INNER JOIN p_conceptoplanilla c ON r.id_conceptoplanilla=c.id_conceptoplanilla
		INNER JOIN p_derechohabiente d ON r.id_derechohabiente=d.id_derechohabiente
		INNER JOIN p_trabajador t ON t.id_trabajador=d.id_trabajador
		INNER JOIN mae_persona p ON p.id_persona=t.id_persona
		INNER JOIN m_cuentacorriente cta ON cta.id_cuentacorriente=r.id_cuentacorriente
		INNER JOIN m_banco ba ON ba.id_banco=cta.id_banco
		WHERE r.id_retencionjudicial=_criterio;
	END IF;
END */$$
DELIMITER ;

/* Procedure structure for procedure `sp_pnllaRetencionJudicialGrid` */

/*!50003 DROP PROCEDURE IF EXISTS  `sp_pnllaRetencionJudicialGrid` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_pnllaRetencionJudicialGrid`(
	_iDisplayStart INT,
        _iDisplayLength INT, 
        _iOrderCol VARCHAR(200),
        _sSearch VARCHAR(200),
        _sFilterCols VARCHAR(300)
)
BEGIN
	DECLARE orderby VARCHAR(300) DEFAULT '';
	DECLARE isearch VARCHAR(300) DEFAULT '';
	DECLARE filterCols VARCHAR(300) DEFAULT '';
	
	IF _sSearch <> '' THEN
		SET isearch = CONCAT(' AND p.numerodocumento = "',_sSearch,'" OR p.nombrecompleto LIKE CONCAT("%',_sSearch,'%")');
	END IF;
	
	IF _sFilterCols <> '' THEN
		SET filterCols = REPLACE(_sFilterCols,'&quot;','"');
		SET filterCols = REPLACE(filterCols,'*','%');
	END IF;
	IF _iOrderCol <> '' THEN
		SET orderby = CONCAT(' ORDER BY ',_iOrderCol);
	END IF;
	
	SET @sentencia = CONCAT('
	SELECT 
		COUNT(*) INTO @countx
	FROM p_retencionjudicial r
	INNER JOIN p_conceptoplanilla c ON r.id_conceptoplanilla=c.id_conceptoplanilla
	INNER JOIN p_derechohabiente d ON r.id_derechohabiente=d.id_derechohabiente
	INNER JOIN p_trabajador t ON t.id_trabajador=d.id_trabajador
	INNER JOIN mae_persona p ON p.id_persona=t.id_persona
	WHERE r.estado<>"E" ',isearch,' ',filterCols,' ;');
	
	PREPARE consulta FROM @sentencia;
	EXECUTE consulta;
	DEALLOCATE PREPARE consulta;
	SET @sentencia = NULL;
	
	SET @sentencia = CONCAT('
	SELECT
		@countx as total,
		r.id_retencionjudicial,
		p.numerodocumento AS dni,
		p.nombrecompleto,
		c.conceptoplanilla,
		CONCAT(d.apellidos,"",d.nombres) AS beneficiario,
		IF(r.tipo_aplicacion="F","Fijo","Porcentaje") AS tipoaplicacion,
		r.fecha_inicio,
		r.fecha_fin,	
		r.monto,
		r.estado
	FROM p_retencionjudicial r
	INNER JOIN p_conceptoplanilla c ON r.id_conceptoplanilla=c.id_conceptoplanilla
	INNER JOIN p_derechohabiente d ON r.id_derechohabiente=d.id_derechohabiente
	INNER JOIN p_trabajador t ON t.id_trabajador=d.id_trabajador
	INNER JOIN mae_persona p ON p.id_persona=t.id_persona
	WHERE r.estado<>"E" 
	',isearch,' ',filterCols,' ',orderby,' LIMIT ',_iDisplayStart,',',_iDisplayLength,'; ');
		
	PREPARE consulta FROM @sentencia;
	EXECUTE consulta;
	DEALLOCATE PREPARE consulta;
	SET @sentencia = NULL;
END */$$
DELIMITER ;

