DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_pnllaRetencionJudicialMantenimiento`(
	_flag 			INT,
	_key 			INT,
	_idderechohabiente 	BIGINT,
	_idcuentacorriente	BIGINT,
	_idconceptoplanilla     BIGINT,
	_fechaInicio		DATE,
	_fechaFin		DATE,
	_tipoAplicacion		CHAR(1),
	_monto			DECIMAL(11,5),
	_observacion		VARCHAR(300),
	_usuario		CHAR(8),
	_estado			CHAR(1)
)
BEGIN
	DECLARE _result INT DEFAULT 1;
	DECLARE _duplicado INT DEFAULT 0;
	
	-- INSERT --
	IF _flag = '1' THEN -- nuevo
		SELECT COUNT(*) INTO _duplicado FROM p_retencionjudicial WHERE id_derechohabiente=_idderechohabiente AND CURDATE()<=fecha_fin AND estado<>'E';
								
		IF _duplicado = 0 THEN
			INSERT INTO p_retencionjudicial (
				id_derechohabiente, 
				id_cuentacorriente, 
				id_conceptoplanilla, 
				fecha_inicio, 
				fecha_fin, 
				tipo_aplicacion, 
				monto, 
				observacion,
				estado,
				usuario_creacion
				) 
			VALUES (
				_idderechohabiente, 
				_idcuentacorriente, 
				_idconceptoplanilla, 
				_fechaInicio, 
				_fechaFin, 
				_tipoAplicacion, 
				_monto, 
				_observacion, 
				_estado, 
				_usuario
				); 
		ELSE 
			SET _result = 2; -- duplicado
		END IF;
		SELECT _result AS result,_duplicado AS duplicado;
	END IF;
	-- UPDATE --
	IF _flag = '2' THEN
		
		SELECT COUNT(*) INTO _duplicado FROM p_retencionjudicial WHERE id_derechohabiente=_idderechohabiente AND CURDATE()<=fecha_fin AND id_retencionjudicial<>_key AND estado<>'E'; 
		
		IF _duplicado = 0 THEN
			UPDATE p_retencionjudicial SET
				id_derechohabiente=_idderechohabiente,
				id_cuentacorriente=_idcuentacorriente,
				id_conceptoplanilla=_idconceptoplanilla,
				fecha_inicio=_fechaInicio,
				fecha_fin=_fechaFin,
				tipo_aplicacion=_tipoAplicacion,
				monto=_monto,
				observacion=_observacion,
				estado=_estado,
				usuario_modifica=_usuario,
				fecha_modifica=NOW()
			WHERE id_retencionjudicial=_key;
		ELSE -- duplicado
			SET _result = 2; 
		END IF;
		SELECT _result AS result,_duplicado AS duplicado;
	END IF;
	-- LOGIC DELETE --
	IF _flag = '3' THEN
		UPDATE p_retencionjudicial SET estado = 'E' WHERE id_retencionjudicial = _key;
		SELECT _result AS result;
	END IF;
	
END */$$
DELIMITER ;

