DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_rolesAccionesMantenimiento`(
	_flag CHAR(1),
	_key CHAR(3),
	_accion VARCHAR(100),
	_alias CHAR(5),
	_activo CHAR(1),
	_icono varchar(50),
	_theme varchar(50),
	_usuario VARCHAR(20)
    )
BEGIN
	DECLARE _duplicaAcc INT DEFAULT 0;
	DECLARE _duplicaAl INT DEFAULT 0;
	DECLARE _result INT DEFAULT 1;
	
	IF _flag = '1' THEN 				SELECT COUNT(*) INTO _duplicaAcc FROM `men_acciones` WHERE `accion` = _accion;
		SELECT COUNT(*) INTO _duplicaAl FROM `men_acciones` WHERE `alias` = _alias;
		
		IF _duplicaAcc = 1 THEN
			SET _result = 2; 		ELSEIF _duplicaAl = 1 THEN
			SET _result = 3; 		ELSE
			INSERT INTO `men_acciones`(
				`id_acciones`,
				`accion`,
				`alias`,
				`activo`,
				`fechasistema`,
				`icono`,
				`theme`,
				`usuario`
			)
			SELECT
				LPAD(MAX(`id_acciones`) + 1,3,0) AS id_acciones,
				_accion,
				_alias,
				_activo,
				NOW(),
				_icono,
				_theme,
				_usuario
			FROM `men_acciones`;
		END IF;
		SELECT _result AS result;
	END IF;
	IF _flag = '2' THEN 		
		SELECT COUNT(*) INTO _duplicaAcc FROM `men_acciones` WHERE `accion` = _accion AND `id_acciones` <> _key;
		SELECT COUNT(*) INTO _duplicaAl FROM `men_acciones` WHERE `alias` = _alias AND `id_acciones` <> _key;
		
		IF _duplicaAcc = 1 THEN
			SET _result = 2; 		ELSEIF _duplicaAl = 1 THEN
			SET _result = 3; 		ELSE
			UPDATE `men_acciones` SET
				`accion` = _accion,
				`alias` = _alias,
				`activo` = _activo,
				`icono` = _icono,
				`theme` = _theme,
				`usuario` = _usuario
			WHERE id_acciones = _key;
		END IF;
		SELECT _result AS result;
	END IF;
	IF _flag = '3' THEN 		
		DELETE FROM `men_acciones` WHERE `id_acciones` = _key;
		SELECT _result AS result;
	END IF;
    END */$$
DELIMITER ;

/* Procedure structure for procedure `sp_rolesConfigurarRolesConsultas` */

/*!50003 DROP PROCEDURE IF EXISTS  `sp_rolesConfigurarRolesConsultas` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_rolesConfigurarRolesConsultas`(
	_flag CHAR(1),
	_criterio VARCHAR(50)
    )
BEGIN
	IF _flag = '1' THEN
		SELECT * FROM `men_rol` WHERE `id_rol` = _criterio;
	END IF;
	IF _flag = '2' THEN -- los dominios
		SELECT id_dominio,dominio FROM `men_dominio` WHERE activo = '1' ORDER BY 2;
	END IF;
	IF _flag = '3' THEN -- los modulos
		SELECT id_modulo,modulo FROM `men_modulo` WHERE id_dominio =  _criterio AND activo = '1' ORDER BY 2;
	END IF;
	IF _flag = '4' THEN -- los menu principales
		SELECT id_menuprincipal,menuprincipal FROM `men_menuprincipal` WHERE id_modulo =  _criterio AND activo = '1' ORDER BY 2;
	END IF;
	IF _flag = '5' THEN -- las opciones de un rol
		SELECT 
			o.`id_menuprincipal`,
			o.`menuprincipal`,
			(SELECT COUNT(*) FROM `men_rolopciones` ro WHERE ro.id_menuprincipal=o.id_menuprincipal AND ro.id_rol=fnExplode(_criterio,'-',1))AS selected,
			(SELECT ro.id_rolopciones FROM `men_rolopciones` ro WHERE ro.id_menuprincipal=o.id_menuprincipal AND ro.id_rol=fnExplode(_criterio,'-',1))AS id_rolopciones
		FROM `men_menuprincipal` o 
		WHERE `id_modulo` =  fnExplode(_criterio,'-',2) AND o.activo = '1' ORDER BY o.orden;
	END IF;
	IF _flag = '6' THEN -- las acciones de una opcion de rol
		SELECT
			id_acciones,accion,alias,IF(selected IS NULL = 1,0,selected)AS selected
		FROM(
			SELECT
				a.`id_acciones`,
				a.`accion`,
				a.`alias`,
				(SELECT aa.activo FROM `men_accionopciones` aa WHERE aa.id_acciones=a.id_acciones AND aa.id_rolopciones=_criterio)AS selected
			FROM `men_acciones` a
			WHERE a.`activo` = '1'
		)AS z;
	END IF;
    END */$$
DELIMITER ;

