/* Procedure structure for procedure `sp_rolesConfigurarRolesMantenimiento` */

/*!50003 DROP PROCEDURE IF EXISTS  `sp_rolesConfigurarRolesMantenimiento` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_rolesConfigurarRolesMantenimiento`(
	_flag CHAR(1),
	_key CHAR(4),
	_rol VARCHAR(100),
	_activo CHAR(1),
	_usuario VARCHAR(20)
    )
BEGIN
	DECLARE _duplica INT DEFAULT 0;
	DECLARE _result INT DEFAULT 1;
	DECLARE _id CHAR(6) DEFAULT '';
	DECLARE _lastRol CHAR(4) DEFAULT '';
	DECLARE _c INT;
	DECLARE _contador INT DEFAULT 0;
	DECLARE _idRolOpcion CHAR(6) DEFAULT '';
	
	IF _flag = '1' THEN -- nuevo
		SELECT COUNT(*) INTO _duplica FROM `men_rol` WHERE `rol` = _rol;
		IF _duplica = 0 THEN
			INSERT INTO `men_rol`(
				`id_rol`,
				`rol`,
				`activo`,
				`fechasistema`,
				`usuario`
			)
			SELECT
				LPAD(MAX(`id_rol`) + 1,4,0) AS id_rol,
				_rol,
				_activo,
				NOW(),
				_usuario
			FROM `men_rol`;
		ELSE -- ya existe
			SET _result = 2;
		END IF;
		SELECT _result AS result;
	END IF;
	IF _flag = '2' THEN -- delete
		DELETE FROM `men_rol` WHERE `id_rol` = _key;
		SELECT _result AS result;
	END IF;
	IF _flag = '3' THEN -- update
		SELECT COUNT(*) INTO _duplica FROM `men_rol` WHERE `rol` = _rol AND id_rol <> _key;
		IF _duplica = 0 THEN
			UPDATE `men_rol` SET
				`rol` = _rol,
				`activo` = _activo
			WHERE `id_rol` = _key;
		ELSE -- ya existe
			SET _result = 2;
		END IF;
		
		
		SELECT _result AS result;
	END IF;
	IF _flag = '4' THEN -- agregar opcion a rol
		-- validar si existe
		SELECT COUNT(*) INTO _duplica FROM men_rolopciones WHERE id_rol=_rol AND id_menuprincipal=_key;
		
		IF _duplica = 0 THEN
			INSERT INTO `men_rolopciones`(
				`id_rolopciones`,
				`id_rol`,
				`id_menuprincipal`,
				`fechasistema`,
				`usuario`
			)
			SELECT
				LPAD((MAX(`id_rolopciones`) + 1),6,0) AS id_rolopciones,
				_rol,
				_key,
				NOW(),
				_usuario
			FROM `men_rolopciones`;
			-- se selecciona id creado
			SELECT MAX(id_rolopciones) INTO _id FROM `men_rolopciones`;
		ELSE -- duplicado
			SET _result = 2;
		END IF;
		SELECT _result AS result,_id AS id_rolopciones;
	END IF;
	IF _flag = '5' THEN -- eliminar opcion a rol
		DELETE FROM `men_rolopciones` WHERE id_rol=_rol AND id_menuprincipal=_key;
		SET _result = 3;
		SELECT _result AS result; 
	END IF;
	IF _flag = '6' THEN -- agregar accion a opcion de un rol
		INSERT INTO `men_accionopciones`(
			`id_acciones`,
			`id_rolopciones`,
			`activo`,
			`fechasistema`,
			`usuario`
		)VALUES(
			_key,
			_rol,
			'1',
			NOW(),
			_usuario
		);
		SELECT _result AS result; 
	END IF;
	IF _flag = '7' THEN -- quitar accion a opcion de un rol
		DELETE FROM men_accionopciones WHERE `id_acciones` = _key AND `id_rolopciones` = _rol;
		SET _result = 3;
		SELECT _result AS result; 
	END IF;
	IF _flag = '8' THEN -- duplicar rol
		INSERT INTO `men_rol`(
			`id_rol`,
			`rol`,
			`activo`,
			`fechasistema`,
			`usuario`
		)
		SELECT
			LPAD(MAX(`id_rol`) + 1,4,0) AS id_rol,
			(SELECT CONCAT(rol,'_copia') FROM men_rol WHERE id_rol = _key),
			'1',
			NOW(),
			_usuario
		FROM `men_rol`;
			
		-- rol creado
		SELECT MAX(id_rol) INTO _lastRol FROM men_rol WHERE `usuario` = _usuario;
		
		
		
		
		
		-- selecciono las opciones de rol a duplicar
		DROP TEMPORARY TABLE IF EXISTS t_opciones;
		CREATE TEMPORARY TABLE t_opciones(
			contadorOP BIGINT(12) PRIMARY KEY AUTO_INCREMENT
		)
		SELECT 
			`id_rolopciones`,
			`id_menuprincipal`
		FROM `men_rolopciones` WHERE id_rol = _key;
		
		-- selecciono todas las acciones de las opciones
		DROP TEMPORARY TABLE IF EXISTS t_acciones;
		CREATE TEMPORARY TABLE t_acciones(
			contadorAC BIGINT(12) PRIMARY KEY AUTO_INCREMENT
		)
		SELECT 
			`id_acciones`,
			`id_rolopciones`
		FROM `men_accionopciones`;
		
		SELECT COUNT(*) INTO _contador FROM t_opciones;
		
		
		
		
		-- agregar las opciones
		SET _c = 1;
		WHILE (_c <= _contador) DO
			
			INSERT INTO `men_rolopciones`(
				`id_rolopciones`,
				`id_rol`,
				`id_menuprincipal`,
				`fechasistema`,
				`usuario`
			)
			SELECT
				LPAD((MAX(`id_rolopciones`) + 1),6,0) AS id_rolopciones,
				_lastRol,
				(SELECT id_menuprincipal FROM t_opciones WHERE contadorOP = _c),
				NOW(),
				_usuario
			FROM `men_rolopciones`;
			-- se selecciona id creado
			SELECT MAX(id_rolopciones) INTO _idRolOpcion FROM `men_rolopciones` WHERE usuario = _usuario;
			
			
			-- acciones de la opcion
			INSERT INTO `men_accionopciones`(
				`id_acciones`,
				`id_rolopciones`,
				`activo`,
				`fechasistema`,
				`usuario`
			)
			SELECT 
				`id_acciones`,
				_idRolOpcion,
				'1',
				NOW(),
				_usuario
			FROM t_acciones WHERE id_rolopciones = (SELECT id_rolopciones FROM t_opciones WHERE contadorOP = _c);
		
		
		
			SET _c=_c + 1;
		END WHILE;
		
		
		
		SELECT _result AS result; 
	END IF;
    END */$$
DELIMITER ;

/* Procedure structure for procedure `sp_sp_conasAsistenciaTrabConsultas` */

/*!50003 DROP PROCEDURE IF EXISTS  `sp_sp_conasAsistenciaTrabConsultas` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_sp_conasAsistenciaTrabConsultas`(
IN _flag 	INT,
IN _criterio	VARCHAR(150)
)
BEGIN
	#1: trabajadores activos
	#2: muestra las fechas un anio
	
	IF _flag = 1 THEN
		SELECT t.id_trabajador,p.nombrecompleto 
		FROM mae_persona p
		INNER JOIN p_trabajador t ON p.id_persona=t.id_persona
		WHERE p.estado='A' AND 
			t.estado='A';
			
	ELSEIF _flag = 2 THEN
		SELECT m.id_asistenciames,
			DATE_FORMAT(m.fecha,'%d-%m-%Y') AS fecha
		FROM m_asisenciames m 
		WHERE LEFT(m.fecha,4)=YEAR(CURDATE())
		AND estado='A';
		
	ELSEIF _flag = 3 THEN
		SELECT a.id_asistenciames,
			a.id_trabajador,
			a.hora_ingreso_m,
			a.hora_salida_m,
			a.hora_ingreso_t,
			a.hora_salida_t
		FROM p_asistenciatrabajador a
		WHERE a.id_asistenciatrabajador=_criterio;
			
	END IF;
END */$$
DELIMITER ;

/* Procedure structure for procedure `sp_sp_conasAsistenciaTrabGrid` */

/*!50003 DROP PROCEDURE IF EXISTS  `sp_sp_conasAsistenciaTrabGrid` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_sp_conasAsistenciaTrabGrid`(
	_idTrabajador BIGINT,
	_iDisplayStart INT,
        _iDisplayLength INT, 
        _iOrderCol VARCHAR(200),
        _sSearch VARCHAR(200),
        _sFilterCols VARCHAR(300)
)
BEGIN
	DECLARE orderby VARCHAR(300) DEFAULT '';
	DECLARE isearch VARCHAR(300) DEFAULT '';
	DECLARE filterCols VARCHAR(300) DEFAULT '';
	/*
	IF _sSearch <> '' THEN
		SET isearch = CONCAT(' AND p.numerodocumento = "',_sSearch,'" OR p.nombrecompleto LIKE CONCAT("%',_sSearch,'%")');
	END IF;
	*/
	
	IF _sFilterCols <> '' THEN
		SET filterCols = REPLACE(_sFilterCols,'&quot;','"');
		SET filterCols = REPLACE(filterCols,'*','%');
	END IF;
	IF _iOrderCol <> '' THEN
		SET orderby = CONCAT(' ORDER BY ',_iOrderCol);
	END IF;
	
	SET @sentencia = CONCAT('
	SELECT COUNT(*) INTO @countx
	FROM p_asistenciatrabajador a
	INNER JOIN p_trabajador t ON a.id_trabajador=t.id_trabajador
	INNER JOIN m_asisenciames am ON am.id_asistenciames=a.id_asistenciames
	WHERE a.estado<>"E" AND
		t.id_trabajador="',_idTrabajador,'"
		',isearch,' ',filterCols,' ;');
	
	PREPARE consulta FROM @sentencia;
	EXECUTE consulta;
	DEALLOCATE PREPARE consulta;
	SET @sentencia = NULL;
	
	SET @sentencia = CONCAT('		
	SELECT @countx as total,
		a.id_asistenciatrabajador,
		t.id_trabajador,
		date_format(am.fecha,"%d-%m-%Y") as fecha,
		a.hora_ingreso_m,
		a.hora_salida_m,
		a.hora_ingreso_t,
		a.hora_salida_t,
		IF(a.origen="S","Sistema","Detector Dactilar") AS origen,
		a.estado
	FROM p_asistenciatrabajador a
	INNER JOIN p_trabajador t ON a.id_trabajador=t.id_trabajador
	INNER JOIN m_asisenciames am ON am.id_asistenciames=a.id_asistenciames
	WHERE a.estado<>"E" AND
		t.id_trabajador="',_idTrabajador,'"
		',isearch,' ',filterCols,' ',orderby,' LIMIT ',_iDisplayStart,',',_iDisplayLength,';
	 ');
		
	PREPARE consulta FROM @sentencia;
	EXECUTE consulta;
	DEALLOCATE PREPARE consulta;
	SET @sentencia = NULL;
END */$$
DELIMITER ;

/* Procedure structure for procedure `sp_sp_conasAsistenciaTrabMantenimiento` */

/*!50003 DROP PROCEDURE IF EXISTS  `sp_sp_conasAsistenciaTrabMantenimiento` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_sp_conasAsistenciaTrabMantenimiento`(
	_flag 			INT,
	_key 			INT,
	_idasistenciames 	BIGINT,
	_idtrabajador		BIGINT,
	_horaingresom	        TIME,
	_horasalidam	        TIME,
	_horaingresot	        TIME,
	_horasalidat	        TIME
)
BEGIN
	DECLARE _result INT DEFAULT 1;
	DECLARE _duplicado INT DEFAULT 0;
	
	DECLARE sql_idTrabajador INT;
	DECLARE sql_fecha VARCHAR(7) DEFAULT '';
	DECLARE sql_total INT DEFAULT 0;
	
	IF _flag = 1 THEN
		#obtenemos la fecha actual a modificar del trabajador
	        SELECT LEFT(fecha,7) INTO sql_fecha FROM m_asisenciames WHERE id_asistenciames=_idasistenciames;
	        
	        #obtenemos los ids y fechas registradas del dia del trabajador
	        DROP TABLE IF EXISTS tmp__diasregistrados;
	        CREATE TEMPORARY TABLE tmp__diasregistrados(INDEX(id_asistenciatrabajador),INDEX(id_asistenciames))
		SELECT a.id_asistenciatrabajador,b.id_asistenciames,a.estado,b.fecha
		FROM p_asistenciatrabajador a
		INNER JOIN m_asisenciames b ON a.id_asistenciames=b.id_asistenciames
		WHERE LEFT(b.fecha,7)=sql_fecha AND a.id_trabajador=_idtrabajador;
		
		SELECT COUNT(*) INTO sql_total FROM tmp__diasregistrados WHERE id_asistenciames=_idasistenciames AND id_asistenciatrabajador<>_key AND estado<>'E';
		
		IF sql_total = 0 THEN
			INSERT INTO p_asistenciatrabajador(id_asistenciames,id_trabajador,hora_ingreso_m,hora_salida_m,hora_ingreso_t,hora_salida_t,origen) 
			VALUES (_idasistenciames, _idtrabajador, _horaingresom, _horasalidam, _horaingresot, _horasalidat, 'S'); 
		ELSE	
			SET _result = 2; -- duplicado
		END IF;
		
		SELECT _result AS result,_duplicado AS duplicado;
		
	END IF;	
	
	IF _flag = 2 THEN
	        SELECT id_trabajador INTO sql_idTrabajador FROM p_asistenciatrabajador WHERE id_asistenciatrabajador=_key;
	        
	        #obtenemos la fecha actual a modificar del trabajador
	        SELECT LEFT(fecha,7) INTO sql_fecha FROM m_asisenciames WHERE id_asistenciames=_idasistenciames;
	         
	        #obtenemos los ids y fechas registradas del dia del trabajador
	        DROP TABLE IF EXISTS tmp__diasregistrados;
	        CREATE TEMPORARY TABLE tmp__diasregistrados(INDEX(id_asistenciatrabajador),INDEX(id_asistenciames))
		SELECT a.id_asistenciatrabajador,b.id_asistenciames,a.estado,b.fecha
		FROM p_asistenciatrabajador a
		INNER JOIN m_asisenciames b ON a.id_asistenciames=b.id_asistenciames
		WHERE LEFT(b.fecha,7)=sql_fecha AND a.id_trabajador=sql_idTrabajador;
	        
	        SELECT COUNT(*) INTO sql_total FROM tmp__diasregistrados WHERE id_asistenciames=_idasistenciames AND id_asistenciatrabajador<>_key AND estado<>'E';
	        
	        IF sql_total = 0 THEN		
			UPDATE p_asistenciatrabajador SET
				id_asistenciames=_idasistenciames,
				hora_ingreso_m=_horaingresom,
				hora_salida_m=_horasalidam,
				hora_ingreso_t=_horaingresot,
				hora_salida_t=_horasalidat
			WHERE id_asistenciatrabajador=_key;
		
		ELSE
			SET _result = 2; 
		END IF;
		SELECT _result AS result,_duplicado AS duplicado;
	
	END IF;
	
	IF _flag = 3 THEN
		UPDATE p_asistenciatrabajador SET estado = 'E' WHERE id_asistenciatrabajador = _key;
		SELECT _result AS result;
	END IF;
END */$$
DELIMITER ;

/* Procedure structure for procedure `sp_usuariosConfigurarUsuariosGrid` */

/*!50003 DROP PROCEDURE IF EXISTS  `sp_usuariosConfigurarUsuariosGrid` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_usuariosConfigurarUsuariosGrid`(
	_iDisplayStart INT,
        _iDisplayLength INT, 
        _iOrderCol VARCHAR(200),
        _sSearch VARCHAR(200)
    )
BEGIN
	DECLARE orderby VARCHAR(300) DEFAULT '';
	DECLARE isearch VARCHAR(300) DEFAULT '';
	
	IF _sSearch <> '' THEN
		SET isearch = CONCAT(' AND p.nombrecompleto LIKE "%',_sSearch,'%" ');
	END IF;
	IF _iOrderCol <> '' THEN
		SET orderby = CONCAT(' ORDER BY ',_iOrderCol);
	END IF;
	
	SET @sentencia = CONCAT('
	SELECT COUNT(*) INTO @countx
	FROM mae_usuario u 
	INNER JOIN mae_persona p ON p.`persona`=u.`persona`
	WHERE u.activo = "1" ',isearch,' ;');
	
	PREPARE consulta FROM @sentencia;
	EXECUTE consulta;
	DEALLOCATE PREPARE consulta;
	SET @sentencia = NULL;
	
	SET @sentencia = CONCAT('
	SELECT 
		@countx as total,
		u.`id_usuario`,
		u.`usuario`,
		p.`nombrecompleto`,
		u.`activo`,
		DATE_FORMAT(u.`ultimo_acceso`,"%d-%m-%Y %H:%m:%s")AS fecha_acceso,
		GROUP_CONCAT(r.rol)AS roles
	FROM `mae_usuario` u
	INNER JOIN mae_persona p ON p.`persona`=u.`persona`
	INNER JOIN `men_usuariorol` ur ON ur.`id_usuario`=u.`id_usuario`
	INNER JOIN men_rol r ON r.`id_rol`=ur.`id_rol`
	WHERE u.activo = "1"
	',isearch,'
	GROUP BY 2
	',orderby,' 
	LIMIT ',_iDisplayStart,',',_iDisplayLength,' ; ');
		
	PREPARE consulta FROM @sentencia;
	EXECUTE consulta;
	DEALLOCATE PREPARE consulta;
	SET @sentencia = NULL;
    END */$$
DELIMITER ;

/* Procedure structure for procedure `sp_usuariosConfigurarUsuariosMantenimiento` */

/*!50003 DROP PROCEDURE IF EXISTS  `sp_usuariosConfigurarUsuariosMantenimiento` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_usuariosConfigurarUsuariosMantenimiento`(
	_flag char(1),
	_key char(8),
	_persona CHAR(8),
	_usuario varchar(100),
	_clave VARCHAR(100),
	_activo char(1),
	_userReg bigint(20)
    )
BEGIN
	DECLARE _duplicado INT DEFAULT 0;
	DECLARE _result INT DEFAULT 1;
	declare _lasIdUser char(8) default'';
	
	if _flag = '1' then -- insert user
		-- duplicidad
		select count(*) into _duplicado from `mae_usuario` where `persona` = _persona and activo = '1';
		if _duplicado = 0 then
			insert into `mae_usuario`(
				`id_usuario`,
				`persona`,
				`usuario`,
				`clave`,
				`activo`,
				`usuarioregistra`
			)
			select 
				LPAD(MAX(`id_usuario`) + 1,8,0),
				_persona,
				_usuario,
				_clave,
				_activo,
				_userReg
			from `mae_usuario`;
			-- last user
			SELECT
				MAX(`id_usuario`) into _lasIdUser
			FROM `mae_usuario`;
		else
			set _result = 2; -- duplicado
		end if;
		select _result as result,_duplicado as duplicado,_lasIdUser as lastIdUser;
	end if;
	IF _flag = '2' THEN -- insert roles
		insert into `men_usuariorol`(
			`id_rol`,
			`id_usuario`
		)values(
			_key,
			_persona
		);
	end if;
	IF _flag = '3' THEN -- update user
		-- duplicidad
		SELECT COUNT(*) INTO _duplicado FROM `mae_usuario` WHERE usuario = _usuario AND id_usuario <> _key and activo = '1';
		
		IF _duplicado = 0 THEN
			UPDATE `mae_usuario` SET
				`usuario` = _usuario,
				`activo` = _activo
			WHERE id_usuario = _key;
			
			-- tambien se actualiza su email en persona
			SELECT `persona` INTO @persona FROM `mae_usuario` WHERE `id_usuario` = _key;
			
			UPDATE `mae_persona` SET `email`= _usuario WHERE `persona` = @persona;
		ELSE
			SET _result = 3; -- duplicado
		END IF;
		SELECT _result AS result,_duplicado AS duplicado;
	END IF;
    END */$$
DELIMITER ;

